sys_variable_value.value:
(function execute(inputs, outputs) {
    // 1. Get Base URL and standard reference components
    var instanceURL = gs.getProperty('glide.servlet.uri');
    var sysId = inputs.sysid;
    var tableName = inputs.table;
    var linkText = inputs.text;
    var urlPath = "";

    // 2. Determine the URL based on the Link Type input
    switch (inputs.type) {
        case "platform":
            // Classic UI/Platform View: nav_to.do?uri=<table>.do?sys_id=<sys_id>
            urlPath = "nav_to.do?uri=" + tableName + ".do?sys_id=" + sysId;
            break;
        case "portal":
            // Service Portal/Employee Center:
            // Assumes 'sp' is the correct portal suffix. Adjust if needed.
            //sp?sys_id=XXX&view=sp&id=ticket&table=incident
            //esc?id=ticket&table=incident&sys_id=XXX
            urlPath = "sp?sys_id=" + sysId +"&view=sp&id=ticket&table="+tableName;
            break;
        case "workspace":
            // Agent Workspace: /now/workspace/record/<table sys_id>/<sys_id>
            urlPath = "now/sow/record/" + tableName + "/" + sysId; 
            //now/sow/record/incident/01fbd2b6c3986a50d9d679ec0501314a
            break;
        default:
            // Default to Platform UI as a fallback
            urlPath = "nav_to.do?uri=" + tableName + ".do?sys_id=" + sysId;
    }

    // 3. Construct the final full URL
    var fullURL = instanceURL + urlPath;
    
    // 4. Construct the HTML Link
    // target="_blank" opens the link in a new tab.
    var htmlLink = '<a href="' + fullURL + '" target="_blank">' + linkText + '</a>';

    // 5. Set the Script Step Outputs
    outputs.url_string = fullURL;
    outputs.link_html = htmlLink;

})(inputs, outputs);

