sys_variable_value.value:
(function execute(inputs, outputs) {
	try {
		// Validate that inputString and delimeter are provided
		if (!inputs.inputString || typeof inputs.inputString !== 'string') {
			gs.error('Convert String to Array: Invalid or missing inputString');
			outputs.error = 'Invalid or missing inputString';
			return;
		}

		if (!inputs.delimeter || typeof inputs.delimeter !== 'string') {
			gs.error('Convert String to Array: Invalid or missing delimiter');
			outputs.error = 'Invalid or missing delimiter';
			return;
		}

		// Handle case where delimiter is not found in inputString
		if (inputs.inputString.indexOf(inputs.delimeter) === -1) {
			gs.info('Convert String to Array: Delimiter not found in string, returning single-element array');
			outputs.converttoarray = [inputs.inputString];
            outputs.success = true;
			return;
		}

		// Perform the conversion
		var resultArray = inputs.inputString.split(inputs.delimeter);

		// Trim spaces from each element
		for (var i = 0; i < resultArray.length; i++) {
			resultArray[i] = resultArray[i].trim();
		}

		// Assign output
		outputs.converttoarray = resultArray;
		outputs.success = true;
		gs.info('Convert String to Array: Conversion successful');
	} catch (ex) {
		// Catch and log any unexpected error
		gs.info('Convert String to Array: Exception occurred - ' + ex.message);
		outputs.error = 'Unexpected error: ' + ex.message;
		outputs.success = false;
	}
})(inputs, outputs);

