sys_variable_value.value:
(function execute(inputs, outputs) {
    //Get list of all manager for user 1
    var managerArr = [];
    var gr1 = new GlideRecord('sys_user');
    if (gr1.get(inputs.user1)) {
        while (gr1.manager) {
            var mgrId = gr1.manager.sys_id.toString();
            managerArr.push(mgrId);
            if (!gr1.get(mgrId)) break; // move up the hierarchy
        }
    }
    // Check if any manager exist in the list for user2
    var commonManager = null;
    var gr2 = new GlideRecord('sys_user');
    if (gr2.get(inputs.user2)) {
        while (gr2.u_manager) {
            commonMgrId = gr2.manager.sys_id.toString();
            if (managerArr.includes(commonMgrId)) {
                commonManager = commonMgrId;
                break;
            }
            if (!gr2.get(commonMgrId)) break; // move up the hierarchy
        }
    }


    if (commonManager) {
        var mgr = new GlideRecord('sys_user');
        mgr.get(commonManager);
        outputs.common_manager = mgr.name.toString();
    //Incase if they don't have manager in common
    } else {
        outputs.common_manager = "They don't have a manager in common.";
    }

})(inputs, outputs);

