sys_variable_value.value:
(function execute(inputs, outputs) {
    
    // Input validation
    if (!inputs.parent_table || !inputs.parent_sys_id || !inputs.child_table || !inputs.relationship_field) {
        outputs.success = false;
        outputs.error_message = "Required inputs missing: parent_table, parent_sys_id, child_table, and relationship_field are mandatory";
        return;
    }
    
    try {
        // Initialize variables
        var parentTable = inputs.parent_table;
        var parentSysId = inputs.parent_sys_id;
        var childTable = inputs.child_table;
        var relationshipField = inputs.relationship_field;
        var aggregateField = inputs.aggregate_field || '';
        var aggregateTypes = inputs.aggregate_types || 'COUNT';
        var filterCriteria = inputs.filter_criteria || '';
        var groupByFields = inputs.group_by_fields || '';
        var additionalConditions = inputs.additional_conditions || '';
        
        // Parse aggregate types
        var requestedAggregates = aggregateTypes.toString().split(',');
        for (var i = 0; i < requestedAggregates.length; i++) {
            requestedAggregates[i] = requestedAggregates[i].trim().toUpperCase();
        }
        
        // Validate aggregate types
        var validAggregates = ['COUNT', 'SUM', 'AVG', 'MAX', 'MIN'];
        for (var j = 0; j < requestedAggregates.length; j++) {
            if (validAggregates.indexOf(requestedAggregates[j]) === -1) {
                outputs.success = false;
                outputs.error_message = "Invalid aggregate type: " + requestedAggregates[j] + ". Valid types are: " + validAggregates.join(', ');
                return;
            }
        }
        
        // For non-COUNT aggregates, aggregate_field is required
        var needsAggregateField = false;
        for (var k = 0; k < requestedAggregates.length; k++) {
            if (requestedAggregates[k] !== 'COUNT') {
                needsAggregateField = true;
                break;
            }
        }
        
        if (needsAggregateField && !aggregateField) {
            outputs.success = false;
            outputs.error_message = "aggregate_field is required for SUM, AVG, MAX, MIN operations";
            return;
        }
        
        // Verify parent record exists
        var parentGR = new GlideRecord(parentTable);
        if (!parentGR.get(parentSysId)) {
            outputs.success = false;
            outputs.error_message = "Parent record not found: " + parentTable + " with sys_id " + parentSysId;
            return;
        }
        
        // Build base query for child records
        var childGR = new GlideRecord(childTable);
        childGR.addQuery(relationshipField, parentSysId);
        
        // Add filter criteria if provided
        if (filterCriteria) {
            try {
                childGR.addEncodedQuery(filterCriteria);
            } catch (filterError) {
                outputs.success = false;
                outputs.error_message = "Invalid filter criteria: " + filterError.message;
                return;
            }
        }
        
        // Add additional conditions if provided
        if (additionalConditions) {
            try {
                childGR.addEncodedQuery(additionalConditions);
            } catch (condError) {
                outputs.success = false;
                outputs.error_message = "Invalid additional conditions: " + condError.message;
                return;
            }
        }
        
        // Initialize results structure
        var results = {
            total_records: 0,
            groups: [],
            overall_aggregates: {}
        };
        
        // Function to calculate aggregates for a set of records
        function calculateAggregates(records, aggregateField, requestedAggregates) {
            var aggregates = {};
            var count = records.length;
            
            // Initialize all requested aggregates
            for (var i = 0; i < requestedAggregates.length; i++) {
                var aggType = requestedAggregates[i];
                switch (aggType) {
                    case 'COUNT':
                        aggregates.count = count;
                        break;
                    case 'SUM':
                        aggregates.sum = 0;
                        break;
                    case 'AVG':
                        aggregates.avg = 0;
                        break;
                    case 'MAX':
                        aggregates.max = null;
                        break;
                    case 'MIN':
                        aggregates.min = null;
                        break;
                }
            }
            
            if (count === 0) {
                return aggregates;
            }
            
            // Calculate aggregates that need field values
            if (aggregateField && needsAggregateField) {
                var sum = 0;
                var validValues = [];
                
                for (var j = 0; j < records.length; j++) {
                    var record = records[j];
                    var value = parseFloat(record[aggregateField]) || 0;
                    
                    if (!isNaN(value)) {
                        validValues.push(value);
                        sum += value;
                        
                        // Track min/max
                        if (aggregates.hasOwnProperty('max')) {
                            if (aggregates.max === null || value > aggregates.max) {
                                aggregates.max = value;
                            }
                        }
                        
                        if (aggregates.hasOwnProperty('min')) {
                            if (aggregates.min === null || value < aggregates.min) {
                                aggregates.min = value;
                            }
                        }
                    }
                }
                
                if (aggregates.hasOwnProperty('sum')) {
                    aggregates.sum = sum;
                }
                
                if (aggregates.hasOwnProperty('avg')) {
                    aggregates.avg = validValues.length > 0 ? sum / validValues.length : 0;
                }
            }
            
            return aggregates;
        }
        
        // Collect all matching records
        var allRecords = [];
        childGR.query();
        
        while (childGR.next()) {
            var record = {};
            
            // Always include the aggregate field if specified
            if (aggregateField) {
                record[aggregateField] = childGR.getValue(aggregateField);
            }
            
            // Include group by fields if specified
            if (groupByFields) {
                var groupFields = groupByFields.split(',');
                for (var m = 0; m < groupFields.length; m++) {
                    var fieldName = groupFields[m].trim();
                    if (fieldName) {
                        record[fieldName] = childGR.getValue(fieldName);
                        record[fieldName + '_display'] = childGR.getDisplayValue(fieldName);
                    }
                }
            }
            
            // Include sys_id for reference
            record.sys_id = childGR.getValue('sys_id');
            allRecords.push(record);
        }
        
        results.total_records = allRecords.length;
        
        // Calculate overall aggregates
        results.overall_aggregates = calculateAggregates(allRecords, aggregateField, requestedAggregates);
        
        // Handle grouping if specified
        if (groupByFields && allRecords.length > 0) {
            var groupFields = groupByFields.split(',');
            var groups = {};
            
            // Group records
            for (var n = 0; n < allRecords.length; n++) {
                var record = allRecords[n];
                var groupKey = '';
                var groupValues = {};
                
                for (var o = 0; o < groupFields.length; o++) {
                    var fieldName = groupFields[o].trim();
                    if (fieldName) {
                        var fieldValue = record[fieldName] || '';
                        groupKey += fieldName + ':' + fieldValue + '|';
                        groupValues[fieldName] = fieldValue;
                        groupValues[fieldName + '_display'] = record[fieldName + '_display'] || fieldValue;
                    }
                }
                
                if (!groups[groupKey]) {
                    groups[groupKey] = {
                        group_values: groupValues,
                        records: []
                    };
                }
                
                groups[groupKey].records.push(record);
            }
            
            // Calculate aggregates for each group
            for (var groupKey in groups) {
                var group = groups[groupKey];
                var groupAggregates = calculateAggregates(group.records, aggregateField, requestedAggregates);
                
                results.groups.push({
                    group_by: group.group_values,
                    record_count: group.records.length,
                    aggregates: groupAggregates
                });
            }
        }
        
        // Set outputs
        outputs.success = true;
        outputs.total_records = results.total_records;
        outputs.results = JSON.stringify(results, null, 2);
        outputs.overall_aggregates = JSON.stringify(results.overall_aggregates, null, 2);
        
        // Individual aggregate outputs for easy access
        if (results.overall_aggregates.count !== undefined) {
            outputs.count = results.overall_aggregates.count;
        }
        if (results.overall_aggregates.sum !== undefined) {
            outputs.sum = results.overall_aggregates.sum;
        }
        if (results.overall_aggregates.avg !== undefined) {
            outputs.average = results.overall_aggregates.avg;
        }
        if (results.overall_aggregates.max !== undefined) {
            outputs.maximum = results.overall_aggregates.max;
        }
        if (results.overall_aggregates.min !== undefined) {
            outputs.minimum = results.overall_aggregates.min;
        }
        
        outputs.groups_count = results.groups.length;
        outputs.error_message = '';
        
    } catch (error) {
        outputs.success = false;
        outputs.error_message = "Unexpected error: " + error.message;
        outputs.total_records = 0;
        outputs.results = '{}';
        outputs.overall_aggregates = '{}';
        outputs.groups_count = 0;
        
        // Log error for debugging
        gs.error("Compute Child Record Aggregates Error: " + error.message + "\nStack: " + error.stack);
    }
    
})(inputs, outputs);

