sys_variable_value.value:
(function execute(inputs, outputs) {

var currentScope = gs.getCurrentScopeName();
var executionStatus = 'Failed';
var scheduleGr = new GlideRecord('sysauto_script');
scheduleGr.addQuery('name', inputs.jobName); 
scheduleGr.query();
if (scheduleGr.next()) {
    if (currentScope.includes('rhino.global')) {
        SncTriggerSynchronizer.executeNow(scheduleGr);
        executionStatus = 'Executed';
    } else {
		
        gs.executeNow(scheduleGr);
        executionStatus = 'Executed';
    }
}
outputs.execution_status = executionStatus;

})(inputs, outputs);

