sys_variable_value.value:
(function execute(inputs, outputs) {
    try {
        var html = inputs.html_content || '<h1>No content provided</h1>';
        var fileName = inputs.file_name || 'Generated_Document.pdf';
        var tableName = inputs.table_name || 'incident';
        var recordSysId = inputs.record_sys_id;

        if (!recordSysId) {
            throw new Error("Missing record_sys_id to attach the PDF.");
        }

        // Optional: Add landscape styling
        var landscape = false;
        var styledHtml = landscape ? '<style>@page {size: A4 landscape;}</style>' + html : html;

        // Generate PDF and attach
        var pdfResult = new sn_pdfgeneratorutils.PDFGenerationAPI().convertToPDF(
            styledHtml,
            tableName,
            recordSysId,
            fileName,
            ''
        );

        if (!pdfResult || !pdfResult.attachment_id) {
            throw new Error("PDF generation failed.");
        }

        var sysId = pdfResult.attachment_id;
        outputs.pdf_attachment_sys_id = sysId;
        outputs.pdf_attachment_link = gs.getProperty('glide.servlet.uri') + 'sys_attachment.do?sys_id=' + sysId;

    } catch (error) {
        gs.error("PDF Generation Error: " + error.message);
        throw new Error("PDF generation failed: " + error.message);
    }
    })(inputs, outputs);

