sys_variable_value.value:
(function execute(inputs, outputs) {


var app = new GlideRecord('sysapproval_approver');
app.addEncodedQuery('state=requested^document_id=' + inputs.task.sys_id +'^approver=' + inputs.existing_app.sys_id);
app.query();
if(app.next()){

    app.approver = inputs.new_app.sys_id;
    app.update();
    
}

// ... code ...
})(inputs, outputs);

