sys_variable_value.value:
(function execute(inputs, outputs) {
    try {
        var table = inputs.record_table;
        var sysId = inputs.record_sys_id;
        var field = inputs.field_name;

        var gr = new GlideRecord(table);
        if (!gr.get(sysId)) {
            outputs.error_message = "Record not found in table: " + table + " with sys_id: " + sysId;
            throw new Error(outputs.error_message);
        }

        var fieldValue = gr.getValue(field);
        if (!fieldValue) {
            outputs.error_message = "Field '" + field + "' is empty or does not exist.";
            throw new Error(outputs.error_message);
        }

        var patterns = {
            "Credit Card": /\b(?:\d[ -]*?){13,16}\b/g,
            "SSN": /\b\d{3}-\d{2}-\d{4}\b/g,
            "Password": /(?=.*password)(?=.*[:=]).*/gi
        };

        var matches = [];

        for (var label in patterns) {
            var regex = patterns[label];
            var found = fieldValue.match(regex);
            if (found) {
                matches.push(label + ": " + found.join(", "));
            }
        }
        var senData=matches.length > 0;
        outputs.contains_sensitive_data = senData.toString();
        outputs.matches_found = matches;
        outputs.error_message = "";

    } catch (err) {
        outputs.contains_sensitive_data = 'false;'
        outputs.matches_found = [];
        outputs.error_message = err.message;
    }
})(inputs, outputs);

