sys_variable_value.value:
(function execute(inputs, outputs) {
    var startTime = new GlideDateTime().getNumericValue();
    
    try {
        var tableName = inputs.table_name ? (inputs.table_name + '') : '';
        
        var recordIdsRaw = inputs.record_sys_ids ? (inputs.record_sys_ids + '').split(',') : [];
        var recordIds = [];
        for (var idx = 0; idx < recordIdsRaw.length; idx++) {
            var id = (recordIdsRaw[idx] + '').trim();
            if (id.length > 0) {
                recordIds.push(id);
            }
        }
        
        // Handle JSON input
        var validationRules;
        try {
            if (typeof inputs.validation_rules === 'object' && inputs.validation_rules !== null) {
                validationRules = inputs.validation_rules;
            } else if (inputs.validation_rules) {
                validationRules = JSON.parse(inputs.validation_rules + '');
            } else {
                throw new Error('Validation rules are required');
            }
        } catch (jsonError) {
            throw new Error('Invalid validation rules JSON: ' + jsonError.message);
        }
        
        var stopOnFirstError = inputs.stop_on_first_error === true || inputs.stop_on_first_error === 'true';
        var includeFieldValues = inputs.include_field_values === true || inputs.include_field_values === 'true';
        var maxRecords = parseInt(inputs.max_records) || 100;
        
        // Validate inputs
        if (!tableName) {
            throw new Error('Table name is required');
        }
        
        if (!gs.tableExists(tableName)) {
            throw new Error('Table "' + tableName + '" does not exist');
        }
        
        if (recordIds.length === 0) {
            throw new Error('No record sys_ids provided');
        }
        
        if (recordIds.length > maxRecords) {
            throw new Error('Number of records (' + recordIds.length + ') exceeds maximum allowed (' + maxRecords + ')');
        }
        
        // Initialize counters
        var totalRecords = recordIds.length;
        var passedCount = 0;
        var failedCount = 0;
        var validationReport = [];
        var failedRecordIds = [];
        var passedRecordIds = [];
        
        // Validate each record
        for (var i = 0; i < recordIds.length; i++) {
            var recordId = recordIds[i];
            var gr = new GlideRecord(tableName);
            
            if (!gr.get(recordId)) {
                validationReport.push({
                    sys_id: recordId,
                    number: 'N/A',
                    status: 'failed',
                    errors: [{
                        field: 'sys_id',
                        rule: 'record_exists',
                        message: 'Record not found'
                    }]
                });
                failedCount++;
                failedRecordIds.push(recordId);
                
                if (stopOnFirstError) {
                    break;
                }
                continue;
            }
            
            var recordNumber = gr.getValue('number') || gr.getValue('name') || recordId;
            var recordErrors = [];
            
            // Validate required fields
            if (validationRules.required_fields && validationRules.required_fields.length > 0) {
                for (var j = 0; j < validationRules.required_fields.length; j++) {
                    var fieldName = validationRules.required_fields[j];
                    var fieldValue = gr.getValue(fieldName);
                    
                    if (!fieldValue || fieldValue === '') {
                        var error = {
                            field: fieldName,
                            rule: 'required_field',
                            message: "Field '" + fieldName + "' is required but empty"
                        };
                        if (includeFieldValues) {
                            error.current_value = fieldValue || '';
                        }
                        recordErrors.push(error);
                    }
                }
            }
            
            // Build record result
            var recordResult = {
                sys_id: recordId,
                number: recordNumber,
                status: recordErrors.length === 0 ? 'passed' : 'failed',
                errors: recordErrors
            };
            
            validationReport.push(recordResult);
            
            if (recordErrors.length === 0) {
                passedCount++;
                passedRecordIds.push(recordId);
            } else {
                failedCount++;
                failedRecordIds.push(recordId);
                
                if (stopOnFirstError) {
                    break;
                }
            }
        }
        
        // Determine overall status
        var overallStatus;
        if (failedCount === 0) {
            overallStatus = 'passed';
        } else if (passedCount === 0) {
            overallStatus = 'failed';
        } else {
            overallStatus = 'partial';
        }
        
        // Calculate execution time
        var endTime = new GlideDateTime().getNumericValue();
        var executionTime = endTime - startTime;
        
        // Build comma-separated ID lists
        var failedIdsString = '';
        for (var f = 0; f < failedRecordIds.length; f++) {
            if (f > 0) failedIdsString += ',';
            failedIdsString += failedRecordIds[f];
        }
        
        var passedIdsString = '';
        for (var p = 0; p < passedRecordIds.length; p++) {
            if (p > 0) passedIdsString += ',';
            passedIdsString += passedRecordIds[p];
        }
        
        // Set outputs
        outputs.validation_status = overallStatus;
        outputs.total_records = totalRecords;
        outputs.passed_count = passedCount;
        outputs.failed_count = failedCount;
        outputs.validation_report = JSON.stringify(validationReport);
        outputs.failed_record_ids = failedIdsString;
        outputs.passed_record_ids = passedIdsString;
        outputs.error_message = '';
        outputs.execution_time_ms = executionTime;
        
    } catch (e) {
        outputs.validation_status = 'error';
        outputs.total_records = 0;
        outputs.passed_count = 0;
        outputs.failed_count = 0;
        outputs.validation_report = '[]';
        outputs.failed_record_ids = '';
        outputs.passed_record_ids = '';
        outputs.error_message = e.message || e.toString();
        outputs.execution_time_ms = 0;
        
        gs.error('Batch Record Validator Error: ' + outputs.error_message);
    }
})(inputs, outputs);

