sys_variable_value.value:
(function execute(inputs, outputs) {
    var grSP = new GlideRecord('sys_properties');
    if (grSP.get('name', inputs.property_name.toString())) {
        grSP.value = inputs.value;
        var res = grSP.update();
        if (res)
            outputs.status = 'Property Updated';
        else
            outputs.status = 'Property Update Failed';
    } else {
        outputs.status = 'Property Not Found';
    }

})(inputs, outputs);

