sys_variable_value.value:
(function execute(inputs, outputs) {

    // Get the sys_email record
    var emailGR = new GlideRecord('sys_email');
    if (emailGR.get(inputs.email)) {
        outputs.from = emailGR.getValue('user_id');       // Sender user sys_id
        outputs.from_address = emailGR.getValue('origemail'); // Raw sender address
        outputs.subject = emailGR.getValue('subject');
        outputs.body_text = emailGR.getValue('body_text');
        outputs.body_html = emailGR.getValue('body');
        outputs.incoming = emailGR.getValue('type') == 'received';
        outputs.email_id = emailGR.getUniqueValue();

        // Extract ticket number or keyword using regex
        var match = outputs.body_text.match(/INC\d{7}/);
        outputs.incident_number = match ? match[0] : '';
    } else {
        outputs.error = 'Invalid sys_email record';
    }

})(inputs, outputs);

