sys_variable_value.value:
(function execute(inputs, outputs) {
    var gr = new GlideRecord('kb_knowledge');
    gr.addQuery('active', true);
    gr.addQuery('short_description', 'CONTAINS', inputs.keyword);
    gr.query();
    var titles = [];
    while (gr.next()) {
        titles.push(gr.short_description.toString());
    }
    outputs.article_titles = JSON.stringify(titles);
})(inputs, outputs);

