sys_variable_value.value:
/*
* This Action queries all "In Progress" Update Sets created by a specific user and sets them to "Ignore". 
*/
(function execute(inputs, outputs) {
    try {
    
        const USER_NAME = inputs.user_name;

        gs.debug('Setting all "in progress" Update Sets belonging to sys_user "' + USER_NAME + '" to "Ignore".')
            
        var grUpdateSet = new GlideRecord('sys_update_set');
        grUpdateSet.addQuery('sys_created_by', USER_NAME);
        grUpdateSet.addQuery('state', 'in progress');
        grUpdateSet.query();

        var ignored = [];
        while (grUpdateSet.next()) {
            grUpdateSet.state = 'ignore';
            if (grUpdateSet.update()) {
                ignored.push(grUpdateSet.getValue('sys_id'));
            } else {
                gs.error('Error updating Update Set with sys_id "'+grUpdateSet.getValue('sys_id')+'"');
            }
        }
        
        gs.debug('Finished ignoring Update Sets - "' + ignored.length + '" have been ignored. Sys IDs: ' + ignored.join(','));
        
        outputs.ignored_count = ignored.length;
        outputs.ignored_ids = ignored.join(',');

    } catch (e) {
        gs.error('ERROR in Action "Ignore User\'s ongoing Update Sets" ' + e.message);
        outputs.ignored_count = -1;
        outputs.ignored_ids = null;
    }
})(inputs, outputs);

