sys_variable_value.value:
/**
 * Hacktoberfest Prototype Notice
 *
 * This code is an initial concept submitted for Hacktoberfest.
 *
 * It is intended as a starting point and needs significant refinement, code review, and testing before it can be considered ready for production or any real-world ServiceNow implementation.
 *
 * The main concern is:
 * - Optimizing JSON parsing, especially for deep/nested structures.
 *
 * Other important areas for improvement include:
 * - Comprehensive error handling and edge case support.
 * - Security reviews, including input validation(JSON Validation) and output sanitization.
 * - Integration with wider ServiceNow Flow Designer best practices.
 *
 *
 */

(function execute(inputs, outputs) {
/**
   * Custom Action Inputs
   *
   * This action accepts the following inputs:
   *
   * @param jsonString    (Type: String, Mandatory: Yes)
   *                       Example: { "user": "Anu", "role": "Admin" }
   *                       The JSON data to parse.
   *
   * @param emailTo       (Type: String, Mandatory: Yes)
   *                       Example: [anu@example.com](mailto:anu@example.com),[manager@example.com](mailto:manager@example.com)
   *                       Comma-separated list of recipients.
   *
   * @param emailSubject  (Type: String, Mandatory: Yes)
   *                       Example: New User Created in ServiceNow
   *                       Custom subject for the email.
   *
   * @param outputFormat  (Type: String, Mandatory: No, Default = text)
   *                       Example: html or text
   *                       Controls formatting of the email body.
   */
function formatJsonText(obj, indent) {
    var emailText = "";
    var spacing = "  ".repeat(indent);

    if (Array.isArray(obj)) {
        obj.forEach(function(item, index) {
            emailText += spacing + "- Item " + (index + 1) + ":\n";
            emailText += formatJsonText(item, indent + 1);
        });
    } else if (typeof obj === "object" && obj !== null) {
        for (var key in obj) {
            if (typeof obj[key] === "object") {
                emailText += spacing + key + ":\n";
                emailText += formatJsonText(obj[key], indent + 1);
            } else {
                emailText += spacing + key + ": " + obj[key] + "\n";
            }
        }
    } else {
        emailText += spacing + obj + "\n";
    }
    return emailText;
}

function formatJsonHtml(obj) {
    var html = "";
    if (Array.isArray(obj)) {
        html += "<ul>";
        obj.forEach(function(item) {
            html += "<li>" + formatJsonHtml(item) + "</li>";
        });
        html += "</ul>";
    } else if (typeof obj === "object" && obj !== null) {
        html += "<table border='1' cellspacing='0' cellpadding='4' style='border-collapse:collapse;'>";
        for (var key in obj) {
            html += "<tr><td><b>" + key + "</b></td><td>" + formatJsonHtml(obj[key]) + "</td></tr>";
        }
        html += "</table>";
    } else {
        html += obj;
    }
    return html;
}

try {
    var parsed = JSON.parse(inputs.jsonString);
    var formatted = "";
    var text = "";
    if (inputs.outputFormat === "html") {
                    text = formatJsonHtml(parsed);
    } else {
        text = formatJsonText(parsed, 0);
    }
    formatted = "Hello,\n\nHere is the parsed JSON data:\n\n" +
                    text +
                    "\nSent from,\nServiceNow Flow Action";
    outputs.emailbody = formatted;
    outputs.status = "Success";

} catch (e) {
    outputs.emailbody = "Error parsing JSON: " + e.message;
    outputs.status = "Failed";
}

})(inputs, outputs);

