sys_variable_value.value:
(function execute(inputs, outputs) {
// Query attachments from table record supplied
var getAttachment = new GlideRecord('sys_attachment');
getAttachment.addQuery('table_name', inputs.sTable ); 
getAttachment.addQuery('table_sys_id', inputs.sSysID );
getAttachment.query();
// Return base64 encoded string of file contents
if (getAttachment.next()) {
    var sa = new GlideSysAttachment();
    var base64Str = sa.getContentBase64(getAttachment);
}
gs.info('Attachment content base64 encoded: ' +base64Str);
// Note this only returns the last attachment if there are multiple
outputs.file_encoded = base64Str;

})(inputs, outputs);

