sys_variable_value.value:
(function execute(inputs, outputs) {
    // Get the incident record
    var incGR = new GlideRecord('incident');
    if (!incGR.get(inputs.incident_sys_id)) {
        return;
    }

    var desc = (incGR.short_description || '').toLowerCase();

    // Define keywords for each group
    var hardwareKeywords = ['hardware', 'laptop', 'pc', 'desktop', 'server', 'printer'];
    var softwareKeywords = ['software', 'application', 'app', 'install', 'update', 'license'];

    var assignedGroup = '';

    // Check hardware keywords
    for (var i = 0; i < hardwareKeywords.length; i++) {
        if (desc.includes(hardwareKeywords[i])) {
            incGR.setValue('assignment_group', '8a5055c9c61122780043563ef53438e3'); // Hardware Group Sys ID
            assignedGroup = 'Hardware';
            break;
        }
    }

    // Check software keywords if no hardware match
    if (!assignedGroup) {
        for (var j = 0; j < softwareKeywords.length; j++) {
            if (desc.includes(softwareKeywords[j])) {
                incGR.setValue('assignment_group', '8a4dde73c6112278017a6a4baf547aa7'); // Software Group Sys ID
                assignedGroup = 'Software';
                break;
            }
        }
    }

    // Update the incident only if a group was assigned
    if (assignedGroup) {
        incGR.update();
        outputs.assigned_group = assignedGroup;
    }

})(inputs, outputs);

