sys_variable_value.value:
(function execute(inputs, outputs) {
    
    // Initialize all outputs to safe defaults for both success and failure paths
    outputs.action_status = 'SUCCESS';
    outputs.error_message = '';
    outputs.primary_records_count = 0;
    outputs.joined_records_count = 0;
    outputs.joined_records_json = '[]';
    
    // Helper function to validate fields within an encoded query string or field list
    function validateQueryFields(gr, encodedQuery, fieldList, tableName) {
        // Validate fields used in the encoded query (handles optional inputs gracefully)
        if (encodedQuery) {
            var pairs = encodedQuery.split('^');
            for (var i = 0; i < pairs.length; i++) {
                // Extract the field name before operators
                var fieldName = pairs[i].split('=')[0].split('<')[0].split('>')[0].split('IN')[0].split('NOTIN')[0].trim();
                if (fieldName.length === 0) continue; 
                
                if (!gr.isValidField(fieldName)) {
                    throw new Error("Query Field Validation Failed: Field '" + fieldName + "' is not a valid column on table " + tableName);
                }
            }
        }
        
        // Validate fields requested for retrieval
        if (fieldList) {
            var fieldsArray = fieldList.split(',');
            for (var i = 0; i < fieldsArray.length; i++) {
                var field = fieldsArray[i].trim();
                if (field.length > 0 && !gr.isValidField(field)) {
                    throw new Error("Output Field Validation Failed: Field '" + field + "' is not a valid column on table " + tableName);
                }
            }
        }
    }


    try {
        // --- 1. Initial Input and Table Validation ---
        
        // Check for basic mandatory null inputs 
        if (!inputs.pTable || !inputs.pQuery || !inputs.pFields || !inputs.pJField || !inputs.jTable || !inputs.jJField || !inputs.jFields) {
            throw new Error("One or more mandatory table or field inputs were empty or null.");
        }

        var grPrimary = new GlideRecord(inputs.pTable);
        // CRITICAL: Ensure Primary Table is valid
        if (!grPrimary.isValid()) {
            throw new Error("Invalid Primary Table name specified: " + inputs.pTable);
        }
        
        // Validate the Primary Join Key Field
        if (!grPrimary.isValidField(inputs.pJField)) {
            throw new Error("Join Key Validation Failed: Primary Join Field '" + inputs.pJField + "' is not a valid column on table " + inputs.pTable);
        }

        // Validate all other Primary Table fields (query and output lists)
        validateQueryFields(grPrimary, inputs.pQuery, inputs.pFields, inputs.pTable);


        // --- Phase 1: Get Primary Records and Collect Unique Join Keys ---
        var primaryRecords = [];
        var joinKeysMap = {}; 
        var primaryFieldsArray = inputs.pFields.split(',');

        grPrimary.addEncodedQuery(inputs.pQuery);
        grPrimary.query();
        
        while (grPrimary.next()) {
            var record = {};
            
            // Collect primary fields
            primaryFieldsArray.forEach(function(field) {
                var fName = field.trim();
                record[fName] = grPrimary.getValue(fName);
            });
            
            // Collect the join key
            var keyValue = grPrimary.getValue(inputs.pJField);
            
            if (keyValue && keyValue.length > 0) {
               record.join_key_value = keyValue; // Temporary key storage for merging
               joinKeysMap[keyValue] = true;
            }
            
            primaryRecords.push(record);
        }
        
        outputs.primary_records_count = primaryRecords.length; 

        var uniqueJoinKeys = Object.keys(joinKeysMap);
        
        // Exit early if no primary records or join keys were found
        if (primaryRecords.length === 0 || uniqueJoinKeys.length === 0) {
            return; 
        }
        
        
        // --- Phase 2: Get Joined Records using the Collected Keys (The Lookup) ---
        var joinedRecordsMap = {}; 
        var grJoined = new GlideRecord(inputs.jTable);

        // CRITICAL: Ensure Joined Table is valid
        if (!grJoined.isValid()) {
            throw new Error("Invalid Joined Table name specified: " + inputs.jTable);
        }

        // Validate the Joined Join Key Field
        if (!grJoined.isValidField(inputs.jJField)) {
            throw new Error("Join Key Validation Failed: Joined Join Field '" + inputs.jJField + "' is not a valid column on table " + inputs.jTable);
        }
        
        // Validate all other Joined Table fields (query and output lists)
        validateQueryFields(grJoined, inputs.jQuery, inputs.jFields, inputs.jTable);
        
        var joinedFieldsArray = inputs.jFields.split(',');
        
        // Build the efficient IN query
        var keyQuery = inputs.jJField + 'IN' + uniqueJoinKeys.join(',');
        
        // Add optional joined query
        if (inputs.jQuery) {
            keyQuery += '^' + inputs.jQuery;
        }
        
        grJoined.addEncodedQuery(keyQuery);
        grJoined.query();
    
        while (grJoined.next()) {
            var joinedData = {};
            joinedFieldsArray.forEach(function(field) {
                var fName = field.trim();
                joinedData[fName] = grJoined.getValue(fName);
            });
            joinedRecordsMap[grJoined.getValue(inputs.jJField)] = joinedData;
        }
    
        // --- Phase 3: Combine and Format the Output ---
        var finalOutput = primaryRecords.map(function(primaryRec) {
            var joinedData = joinedRecordsMap[primaryRec.join_key_value] || {};
            
            // Manual merge
            var finalRecord = {};
            for (var key in primaryRec) {
                if (key !== 'join_key_value') { 
                    finalRecord[key] = primaryRec[key];
                }
            }
            for (var key in joinedData) {
                finalRecord[key] = joinedData[key];
            }
    
            return finalRecord;
        });
    
        // --- Final Success Output Assignment ---
        outputs.joined_records_count = finalOutput.length;
        outputs.joined_records_json = JSON.stringify(finalOutput);

    } catch (ex) {
        // --- Error Evaluation Block (FAILURE Path) ---
        outputs.action_status = 'FAILURE';
        
        // Use ex.toString() to reliably capture the descriptive platform error text.
        var fullErrorString = ex.toString(); 
        outputs.error_message = 'Join Action failed: ' + fullErrorString;
        
        // Ensure data outputs are zeroed out or cleared on failure
        outputs.joined_records_json = '[]';
        outputs.joined_records_count = 0;
        
       // gs.error('JOIN Action Error: ' + fullErrorString, 'CustomJoinAction');
    }
})(inputs, outputs);

