sys_variable_value.value:
(function execute(inputs, outputs) {
	
	var url = inputs.url;
	var title = inputs.title;
	var createdCount = 0;
    var updatedCount = 0; // New counter for tracking updates
    var favoriteTable = 'sys_ui_bookmark';
    var userGr = inputs.users_list; // Input of type List (Users) is of type GlideRecord object
		
    while(userGr._next()){
        // Build userDetails record
        var userDetails = {
            name: userGr.getValue("name"),
            userSysId: userGr.getValue("sys_id"),
        };
		
        //  Check if the favorite already exists (to prevent duplicates)
             var grFavorite = new GlideRecord(favoriteTable);
             grFavorite.addQuery('user', userGr.getValue("sys_id"));
            grFavorite.addQuery('url', url); // Unique identifier for the link
            grFavorite.query();
			if (grFavorite.next()) {
            // *** UPDATE EXISTING RECORD LOGIC ***
            // Found an existing favorite for this user/URL, update the title.
                grFavorite.setValue('title', title);
                grFavorite.update();
                updatedCount++;
				
            } else {
                // *** INSERT NEW RECORD LOGIC ***
                // No existing record found, create a new one.
                var grNewFavorite = new GlideRecord(favoriteTable);
                grNewFavorite.setValue('user', userGr.getValue("sys_id"));
                grNewFavorite.setValue('title', title);
                grNewFavorite.setValue('url', url);
				grNewFavorite.setValue('ui_type','all');
				grNewFavorite.setValue('color','white');
				grNewFavorite.setValue('icon','star');
                grNewFavorite.insert();
                createdCount++;
            }        
    }
	outputs.created_count = createdCount;
    outputs.updated_count = updatedCount; // Return the update count
	
})(inputs, outputs);

