sys_variable_value.value:
(function execute(inputs, outputs) {
  try {
    // Normalize and validate required inputs
    var tableName = String(inputs.table_name || '').trim();
    var conditions = String(inputs.conditions || '').trim();
    var fieldName = String(inputs.field || '').trim();
    var aggregateType = String(inputs.aggregate_type || '').trim().toUpperCase();

    // Input validation
    if (!tableName) {
      outputs.success = false;
      outputs.error = 'Missing required input: table_name';
      return;
    }
    if (!fieldName) {
      outputs.success = false;
      outputs.error = 'Missing required input: field';
      return;
    }
    if (!aggregateType) {
      outputs.success = false;
      outputs.error = 'Missing required input: aggregate_type';
      return;
    }

    // Validate table exists
    var tableValidator = new GlideRecord(tableName);
    if (!tableValidator.isValid()) {
      outputs.success = false;
      outputs.error = 'Invalid table: ' + tableName;
      return;
    }

    // Validate field exists on the table
    if (!tableValidator.isValidField(fieldName)) {
      outputs.success = false;
      outputs.error = 'Invalid field: ' + fieldName + ' on table: ' + tableName;
      return;
    }

    // Get field type from sys_dictionary
    var fieldType = null;
    var fieldInfo = new GlideRecord('sys_dictionary');
    fieldInfo.addQuery('name', tableName);
    fieldInfo.addQuery('element', fieldName);
    fieldInfo.setLimit(1);
    fieldInfo.query();
    if (fieldInfo.next()) {
      fieldType = fieldInfo.getValue('internal_type');
    }



    // Initialize result object with all possible aggregates
    var aggregateResult = {
      count: null,
      sum: null,
      avg: null,
      max: null,
      min: null,
      stddev: null,
      group_concat: null,
      group_concat_distinct: null,
      aggregate_type_requested: aggregateType
    };

    // Validate aggregate type is one of the allowed values
    var allowedTypes = ['COUNT', 'SUM', 'AVG', 'MAX', 'MIN', 'STDDEV', 'GROUP_CONCAT', 'GROUP_CONCAT_DISTINCT', 'ALL'];
    if (allowedTypes.indexOf(aggregateType) === -1) {
      outputs.success = false;
      outputs.error = 'Invalid aggregate_type: ' + aggregateType + '. Allowed values: ' + allowedTypes.join(', ');
      return;
    }

    // Determine which aggregates are applicable based on field type
    var isNumericField = ['integer', 'decimal', 'float', 'currency', 'percent', 'price'].indexOf(fieldType) !== -1;
    var skippedAggregates = [];
    var applicableAggregates = [];

    // Numeric aggregates only work on numeric fields
    var numericAggregates = ['SUM', 'AVG', 'STDDEV', 'MAX', 'MIN'];
    var stringAggregates = ['GROUP_CONCAT', 'GROUP_CONCAT_DISTINCT', 'COUNT'];

    if (aggregateType === 'ALL') {
      // For ALL, add only applicable aggregates based on field type
      aggregateResult.count = 0; // COUNT always works
      applicableAggregates.push('COUNT');

      if (isNumericField) {
        numericAggregates.forEach(function(agg) {
          applicableAggregates.push(agg);
        });
      } else {
        numericAggregates.forEach(function(agg) {
          skippedAggregates.push(agg + ' (not applicable for ' + fieldType + ' field)');
        });
      }

      stringAggregates.filter(function(agg) {
        return agg !== 'COUNT';
      }).forEach(function(agg) {
        applicableAggregates.push(agg);
      });
    } else {
      // Single aggregate requested
      if (aggregateType === 'COUNT') {
        applicableAggregates.push('COUNT');
      } else if (numericAggregates.indexOf(aggregateType) !== -1 && !isNumericField) {
        skippedAggregates.push(aggregateType + ' (not applicable for ' + fieldType + ' field)');
      } else {
        applicableAggregates.push(aggregateType);
      }
    }

    // If no applicable aggregates, return error
    if (applicableAggregates.length === 0) {
      outputs.success = false;
      outputs.error = 'No applicable aggregates for field type: ' + fieldType + '. Requested: ' + aggregateType;
      outputs.skipped_aggregates = skippedAggregates;
      return;
    }

    // Create GlideAggregate instance
    var aggregator = new GlideAggregate(tableName);
    if (conditions) {
      aggregator.addEncodedQuery(conditions);
    }
    aggregator.setGroup(false);

    // Add only applicable aggregates
    applicableAggregates.forEach(function(agg) {
      aggregator.addAggregate(agg, fieldName);
    });

    // Execute the aggregate query
    aggregator.query();

    // Process results
    if (aggregator.next()) {
      applicableAggregates.forEach(function(agg) {
        var rawValue = aggregator.getAggregate(agg, fieldName);

        switch (agg) {
          case 'COUNT':
            aggregateResult.count = parseInt(rawValue) || 0;
            break;
          case 'SUM':
            aggregateResult.sum = parseFloat(rawValue) || 0;
            break;
          case 'AVG':
            aggregateResult.avg = parseFloat(rawValue) || 0;
            break;
          case 'MAX':
            aggregateResult.max = isNumericField ? (parseFloat(rawValue) || null) : (rawValue || null);
            break;
          case 'MIN':
            aggregateResult.min = isNumericField ? (parseFloat(rawValue) || null) : (rawValue || null);
            break;
          case 'STDDEV':
            aggregateResult.stddev = parseFloat(rawValue) || 0;
            break;
          case 'GROUP_CONCAT':
            aggregateResult.group_concat = String(rawValue || '');
            break;
          case 'GROUP_CONCAT_DISTINCT':
            aggregateResult.group_concat_distinct = String(rawValue || '');
            break;
        }
      });

      outputs.success = true;
      outputs.message = 'Aggregate(s) calculated successfully. Field type: ' + fieldType;
      if (skippedAggregates.length > 0) {
        outputs.message += '. Skipped: ' + skippedAggregates.join('; ');
      }
      outputs.aggregates = aggregateResult;
      outputs.skipped_aggregates = skippedAggregates;
    } else {
      // No records matched the conditions
      outputs.success = true;
      outputs.message = 'No records found matching the specified conditions. Field type: ' + fieldType;
      if (skippedAggregates.length > 0) {
        outputs.message += '. Skipped: ' + skippedAggregates.join('; ');
      }
      outputs.aggregates = aggregateResult;
      outputs.skipped_aggregates = skippedAggregates;
    }

  } catch (error) {
    gs.error('GlideAggregate Execution Error: ' + error);
    outputs.success = false;
    outputs.error = 'Error during aggregation: ' + error.toString();
  }
})(inputs, outputs);

