sys_variable_value.value:
(function execute(inputs, outputs) {
 
 var grRecord = new GlideAggregate(inputs.tableName);
grRecord.addAggregate('COUNT', inputs.fieldName);
grRecord.groupBy('name'); 
grRecord.query();
var duplicateInfo = [];
while (grRecord.next()) {
	var obj = {};
    if (grRecord.getAggregate('COUNT', inputs.fieldName) > 1) {
      
		obj.name = grRecord.name;
		obj.count = grRecord.getAggregate('COUNT', inputs.fieldName);
		duplicateInfo.push(obj);
    }
}
//gs.info("Final information:"+duplicateInfo);
outputs.duplicate_records = duplicateInfo;
 
 })(inputs, outputs);

