sys_variable_value.value:
(function execute(inputs, outputs) {

var group_sys = inputs.grp_sys_id; //always populate this with the group sys_id
var user_sys = inputs.usr_sys_id; //populate this if you want to verify whether user passes iAccessHandler checks, otherwise leave it blank!

var group = new GlideRecord('sys_user_group');
group.get(group_sys);
var output = '';
var final_role_list = [];
var roles_without_assignable_by = [];
var exclusion_list = [];

function checkRole(role_sys){
var role = new GlideRecord('sys_user_role');
role.get(role_sys);
if(role.sys_scope.scoped_administration){
output += '\n' + role.name + ' will trigger iAccessHandler, ' + role.sys_scope.getDisplayValue() + ' has Application Administration activated.' + (role.assignable_by ? ' Assignable by: ' + role.assignable_by.getDisplayValue() : ' NOTE Assignable by is empty, role cannot be inherited without this set');
if(role.assignable_by && final_role_list.indexOf(role.assignable_by.getDisplayValue())
== -1){
final_role_list.push(role.assignable_by.getDisplayValue());
}
else if(!role.assignable_by && roles_without_assignable_by.indexOf(role.name + '') == -1){
roles_without_assignable_by.push(role.name + '');
}
}
var contains = new GlideRecord('sys_user_role_contains');
contains.addEncodedQuery('role=' + role.sys_id + '^role.nameNOT IN' + exclusion_list.toString(
));
contains.query();
exclusion_list.push(role.name + '');
if(contains.getRowCount() > 0){
while(contains.next()){
checkRole(contains.contains + '');
}
}
else
return;
}

var grp_role = new GlideRecord('sys_group_has_role');
grp_role.addEncodedQuery('group=' + group.sys_id);
grp_role.query();
while(grp_role.next()){
//start checking role hierarchy
checkRole(grp_role.role + '');
}
gs.info(output);
if(final_role_list.length > 0)
//gs.info('Final role list: ' + final_role_list.join(', '));
outputs.role_list = final_role_list.join(', ');
if(roles_without_assignable_by.length > 0)
//gs.info('Roles without Assignable By (any roles in this list cannot be inherited unless App Administration is unchecked for scope OR Assignable by is populated): ' + roles_without_assignable_by.join(', '));
outputs.role_no_assignable_by = roles_without_assignable_by.join(', ');
if(user_sys){
var missing_roles = [];
var user = new GlideRecord('sys_user');
if(user.get(user_sys)){
for(var roles in final_role_list){
var has_role = new GlideRecord('sys_user_has_role');
has_role.addEncodedQuery('user=' + user.sys_id + '^role.name=' + final_role_list[roles]);
has_role.query();
if(!has_role.next() && missing_roles.indexOf(final_role_list[roles]) == -1){
missing_roles.push(final_role_list[roles]);
}
}
if(missing_roles.length > 0)
//gs.info('User: ' + user.name + ' is missing: ' + missing_roles.join(', '));
outputs.missing_roles = missing_roles.join(', ');
else if(roles_without_assignable_by.length >0){
//gs.info('User: ' + user.name + ' has all roles needed for iAccessHandler, but will not be able to pass iAccessHandler due to the Roles without Assignable by identified.');
outputs.iaccess_check = 'User: ' + user.name + ' has all roles needed for iAccessHandler, but will not be able to pass iAccessHandler due to the Roles without Assignable by identified.';
}
else
//gs.info('User: ' + user.name + ' has all roles needed for iAccessHandler.');
outputs.user_has_roles = 'User: ' + user.name + ' has all roles needed for iAccessHandler.'
}
}



// ... code ...
})(inputs, outputs);

