sys_variable_value.value:
(function execute(inputs, outputs) {

    var job2Run = new GlideRecord('scheduled_import_set');
    job2Run.get(inputs.scheduled_import);

    if (job2Run) {
        var scriptOutput = SncTriggerSynchronizer.executeNow(job2Run); //Run import and store the output

        if(!scriptOutput) {
            outputs.import_ran = false; //If output is empty, the data import didn't run
        } else {
            outputs.import_ran = true;
        }

    }


})(inputs, outputs);

