sys_variable_value.value:
(function execute(inputs, outputs) {
    try {
        if (inputs.count == 1) { //if excel attachment found
            var attachmentSID = inputs.attachment; //  Attachment sys_id which is passed as an input
            var parser = new sn_impex.GlideExcelParser();
            var attachment = new GlideSysAttachment();
            var attachmentStream = attachment.getContentStream(attachmentSID);
            parser.parse(attachmentStream);

            var data = [];
            while (parser.next()) {
                var row = parser.getRow();
                data.push(row);
            }
            outputs.jsonrows = JSON.stringify(data);
            if (data.length == 0)
                outputs.message = 'Attachment is Empty with 0 Rows'
            else
                outputs.message = 'Attachment parsed with' + data.length + ' Rows';

           
        }
		else
        {
            outputs.message = 'Attachment is not Found';
        }
		
    } catch (ex) {
        var message = ex.message;
        outputs.message = 'Attachment parse Failed with error: ' + message;
		
    }

})(inputs, outputs);

