sys_variable_value.value:
(function execute(inputs, outputs) {


   
    function toBool(val) {
        if (typeof val === 'boolean') return val;
        if (typeof val === 'string') return val.toLowerCase() === 'true';
        return false; 
    }


    
    var length = inputs.length ;
    var includeUpper = toBool(inputs.include_uppercase_letters);
    var includeLower = toBool(inputs.include_lowercase_letters);
    var includeNumbers = toBool(inputs.include_numbers);
    var includeSpecial = toBool(inputs.include_special_characters);
    var excludeAmbiguous = toBool(inputs.exclude_ambiguous_characters || 'false');
    var specialChars = inputs.special_characters_list || '!@#$%^&*';


    var charset = '';
    var charsets = [];

    if (includeUpper) {
        var upper = excludeAmbiguous ? 'ABCDEFGHJKLMNPQRSTUVWXYZ' : 'ABCDEFGHIJKLMNOPQRSTUVWXYZ';
        charset += upper;
        charsets.push(upper);
    }


    if (includeLower) {
        var lower = excludeAmbiguous ? 'abcdefghijkmnopqrstuvwxyz' : 'abcdefghijklmnopqrstuvwxyz';
        charset += lower;
        charsets.push(lower);
    }


    if (includeNumbers) {
        var numbers = excludeAmbiguous ? '23456789' : '0123456789';
        charset += numbers;
        charsets.push(numbers);
    }


    if (includeSpecial) {
        charset += specialChars;
        charsets.push(specialChars);
    }

    if (!charset || charset.length === 0) {
        gs.warn('No character types selected — returning empty string');
        outputs.random_string = '';
        outputs.entropy_bits = 0;
        outputs.character_types_used = 0;
        return;
    }

    if (length < charsets.length) length = charsets.length;


    // Step 1: guarantee one char from each selected type sets
    var resultArray = [];
    for (var i = 0; i < charsets.length; i++) {
        var set = charsets[i];
        var idx = Math.floor(Math.random() * set.length);
        resultArray.push(set.charAt(idx));
    }


    // Step 2: fill up to total length from full charset
    for (var j = resultArray.length; j < length; j++) {
        var idx2 = Math.floor(Math.random() * charset.length);
        resultArray.push(charset.charAt(idx2));
    }


    // Step 3: shuffle array for unpredictability (Fisher-Yates shuffle)
    for (var k = resultArray.length - 1; k > 0; k--) {
        var r = Math.floor(Math.random() * (k + 1));
        var temp = resultArray[k];
        resultArray[k] = resultArray[r];
        resultArray[r] = temp;
    }


    // Step 4: calculate entropy bits (approximate)
    var entropyBits = Math.floor(length * (Math.log(charset.length) / Math.log(2)));



    outputs.random_string = resultArray.join('');
    outputs.entropy_bits = entropyBits;
    outputs.character_types_used = charsets.length;
})(inputs, outputs);

