sys_variable_value.value:
(function execute(inputs, outputs) {
    if(inputs.status == '200'){
        try{
            var resp_json = JSON.parse(inputs.response);
            var ai_response = resp_json.candidates[0].content.parts[0].text;
            outputs.response = ai_response;
        } catch(ex){
            outputs.error = "Gemini API call response processing failed with error:" + ex;
            throw "Gemini API call response processing failed with error:" + ex;
        }
    }else{
        outputs.error = "Gemini API call Failed with HTTP Status code: " + inputs.status;    
        throw "Gemini API call Failed with HTTP Status code: " + inputs.status;
    }
})(inputs, outputs);

