sys_variable_value.value:
(function execute(inputs, outputs) {
    // Check file type
    var supported_file_types = ["image/png", "image/jpeg", "image/webp", "image/heic", "image/heif"];
    outputs.image_1_content_type = inputs.image_1.getValue("content_type");
    outputs.image_2_content_type = inputs.image_2.getValue("content_type");
    if(supported_file_types.indexOf(outputs.image_1_content_type) == -1 || supported_file_types.indexOf(outputs.image_2_content_type) == -1){
        outputs.error = "Atleast one of the image provided is of Unsupported image type.";
        throw 'Atleast one of the image provided is of Unsupported image type.'
    }else{
        try{ 
            var image_1_base64 = new GlideSysAttachment().getContentBase64(inputs.image_1); // Get base64 string of file content
            if(!gs.nil(image_1_base64)){ // Check if base64 is returned or falsy value
                outputs.image_1_base64 = image_1_base64;
                outputs.image_1_file_name = inputs.image_1.getValue("file_name");
                outputs.image_1_file_size_in_bytes = inputs.image_1.getValue("size_in_bytes");
            }else{
                outputs.error_image_1 = "Image 1 - File size is more than 5 MB";
                throw "Image 1 - File size is more than 5 MB";
            }

            var image_2_base64 = new GlideSysAttachment().getContentBase64(inputs.image_2); // Get base64 string of file content
            if(!gs.nil(image_2_base64)){ // Check if base64 is returned or falsy value
                outputs.image_2_base64 = image_2_base64;
                outputs.image_2_file_name = inputs.image_2.getValue("file_name");
                outputs.image_2_file_size_in_bytes = inputs.image_2.getValue("size_in_bytes");
            }else{
                outputs.error_image_2 = "Image 2 - File size is more than 5 MB";
                throw "Image 2 - File size is more than 5 MB";
            }
        } catch(ex){
            outputs.error = "Run time error: " + ex;
            throw "Run time error: " + ex;
        }
    }

})(inputs, outputs);

