sys_hub_input_scripts.script:
{"body":{"scriptActive":true,"script":"/*
**Access Flow/Action data using the fd_data object. Script must return a value. 
**Order number is offset by +1 in Error Handling Section.
**Available options display upon pressing "." after fd_data
**example: var shortDesc = fd_data.trigger.current.short_description;
**return shortDesc;
*/

var data_body = {
 contents: [{
 parts:[{
 "inline_data": {
 "mime_type": fd_data._1__validate_image_and_get_base_64.image_1_content_type,
 "data": fd_data._1__validate_image_and_get_base_64.image_1_base64
 }
 },
 {
 "inline_data": {
 "mime_type": fd_data._1__validate_image_and_get_base_64.image_2_content_type,
 "data": fd_data._1__validate_image_and_get_base_64.image_2_base64
 }
 },
 {
 "text": fd_data._2__process_prompt.prompt
 }
 ]
 }
 ]
};

return JSON.stringify(data_body);"}}

sys_variable_value.value:
(function execute(inputs, outputs) {
    var prompt = inputs.prompt;
    if(gs.nil(prompt)){
        outputs.prompt = "Compare the images ind detail and provide your findings.";
    }else{
        outputs.prompt = prompt;
    }
})(inputs, outputs);

