sys_variable_value.value:
(function execute(inputs, outputs) {
    var startTime = new GlideDateTime().getNumericValue();
    
    try {
        // Parse inputs.
        var operationMode = inputs.operation_mode + '';
        var attachmentSysId = inputs.attachment_sys_id + '';
        var hashAlgorithm = inputs.hash_algorithm ? (inputs.hash_algorithm + '') : 'SHA-256';
        var secondAttachmentSysId = inputs.second_attachment_sys_id ? (inputs.second_attachment_sys_id + '') : '';
        var expectedHash = inputs.expected_hash ? (inputs.expected_hash + '') : '';
        var searchScope = inputs.search_scope ? (inputs.search_scope + '') : 'global';
        var tableName = inputs.table_name ? (inputs.table_name + '') : '';
        var recordSysId = inputs.record_sys_id ? (inputs.record_sys_id + '') : '';
        var includeMetadata = inputs.include_metadata === true || inputs.include_metadata === 'true';
        
        // Validate inputs.
        if (!operationMode) {
            throw new Error('Operation mode is required');
        }
        
        if (!attachmentSysId) {
            throw new Error('Attachment sys_id is required');
        }
        
        // Initialize outputs
        outputs.operation_status = 'error';
        outputs.hash_value = '';
        outputs.hash_algorithm_used = hashAlgorithm;
        outputs.match_found = false;
        outputs.duplicate_count = 0;
        outputs.duplicate_attachments = '[]';
        outputs.hashes_match = false;
        outputs.first_attachment_hash = '';
        outputs.second_attachment_hash = '';
        outputs.verification_result = 'error';
        outputs.attachment_metadata = '{}';
        outputs.error_message = '';
        
        // Get attachment record
        var attGr = new GlideRecord('sys_attachment');
        if (!attGr.get(attachmentSysId)) {
            outputs.operation_status = 'not_found';
            outputs.error_message = 'Attachment not found: ' + attachmentSysId;
            return;
        }
        
        // Calculate hash for the attachment
        var hash = calculateAttachmentHash(attachmentSysId, hashAlgorithm);
        if (!hash) {
            throw new Error('Failed to calculate hash for attachment');
        }
        
        outputs.hash_value = hash;
        outputs.first_attachment_hash = hash;
        
        // Get attachment metadata if requested
        if (includeMetadata) {
            var metadata = {
                file_name: attGr.getValue('file_name'),
                size_bytes: parseInt(attGr.getValue('size_bytes')),
                content_type: attGr.getValue('content_type'),
                table_name: attGr.getValue('table_name'),
                table_sys_id: attGr.getValue('table_sys_id'),
                created: attGr.getValue('sys_created_on'),
                created_by: attGr.getValue('sys_created_by'),
                hash: hash
            };
            outputs.attachment_metadata = JSON.stringify(metadata);
        }
        
        // Process based on operation mode
        if (operationMode === 'calculate_hash') {
            // Just return the hash
            outputs.operation_status = 'success';
            
        } else if (operationMode === 'find_duplicates') {
            // Find duplicate attachments with same hash
            var duplicates = findDuplicateAttachments(
                attachmentSysId, 
                hash, 
                searchScope, 
                tableName, 
                recordSysId,
                includeMetadata
            );
            
            outputs.duplicate_count = duplicates.length;
            outputs.match_found = duplicates.length > 0;
            outputs.duplicate_attachments = JSON.stringify(duplicates);
            outputs.operation_status = 'success';
            
        } else if (operationMode === 'compare_hashes') {
            // Compare two attachments
            if (!secondAttachmentSysId) {
                throw new Error('Second attachment sys_id is required for comparison');
            }
            
            var secondAttGr = new GlideRecord('sys_attachment');
            if (!secondAttGr.get(secondAttachmentSysId)) {
                outputs.operation_status = 'not_found';
                outputs.error_message = 'Second attachment not found: ' + secondAttachmentSysId;
                return;
            }
            
            var secondHash = calculateAttachmentHash(secondAttachmentSysId, hashAlgorithm);
            if (!secondHash) { 
                throw new Error('Failed to calculate hash for second attachment');
            }
            
            outputs.second_attachment_hash = secondHash;
            outputs.hashes_match = (hash === secondHash);
            outputs.match_found = outputs.hashes_match;
            outputs.operation_status = 'success';
            
        } else if (operationMode === 'verify_hash') {
            // Verify against expected hash
            if (!expectedHash) {
                throw new Error('Expected hash is required for verification');
            }
            
            var hashesMatch = (hash.toLowerCase() === expectedHash.toLowerCase());
            outputs.hashes_match = hashesMatch;
            outputs.match_found = hashesMatch;
            outputs.verification_result = hashesMatch ? 'verified' : 'mismatch';
            outputs.operation_status = 'success';
            
        } else {
            throw new Error('Invalid operation mode: ' + operationMode);
        }
        
        // Calculate execution time
        var endTime = new GlideDateTime().getNumericValue();
        outputs.execution_time_ms = endTime - startTime;
        
    } catch (e) {
        outputs.operation_status = 'error';
        outputs.error_message = e.message || e.toString();
        outputs.execution_time_ms = 0;
        gs.error('Attachment Hash Matcher Error: ' + outputs.error_message);
    }
    
    // Helper function to calculate attachment hash
    function calculateAttachmentHash(sysId, algorithm) {
        try {
            var sa = new GlideSysAttachment();
            var attachmentStream = sa.getContentStream(sysId);
            
            if (!attachmentStream) {
                gs.error('Could not get content stream for attachment: ' + sysId);
                return null;
            }
            
            // Use GlideDigest for hashing
            var digest = new GlideDigest();
            var hashValue;
            
            if (algorithm === 'MD5') {
                hashValue = digest.getMD5Base64FromInputStream(attachmentStream);
            } else if (algorithm === 'SHA-1') {
                hashValue = digest.getSHA1Base64FromInputStream(attachmentStream);
            } else if (algorithm === 'SHA-256') {
                hashValue = digest.getSHA256Base64FromInputStream(attachmentStream);
            } else {
                gs.error('Unsupported hash algorithm: ' + algorithm);
                return null;
            }
            
            return hashValue;
            
        } catch (e) {
            gs.error('Error calculating hash: ' + e.message);
            return null;
        }
    }
    
    // Helper function to find duplicate attachments
    function findDuplicateAttachments(excludeSysId, hash, scope, table, recordId, includeMeta) {
        var duplicates = [];
        
        try {
            // We need to calculate hash for each attachment and compare
            // This is expensive, so we'll limit the search scope
            
            var attGr = new GlideRecord('sys_attachment');
            
            // Apply scope filters
            if (scope === 'table' && table) {
                attGr.addQuery('table_name', table);
            } else if (scope === 'record' && table && recordId) {
                attGr.addQuery('table_name', table);
                attGr.addQuery('table_sys_id', recordId);
            }
            
            // Exclude the original attachment
            attGr.addQuery('sys_id', '!=', excludeSysId);
            
            // Limit results for performance
            attGr.setLimit(100);
            attGr.query();
            
            while (attGr.next()) {
                var currentHash = calculateAttachmentHash(attGr.getValue('sys_id'), hashAlgorithm);
                
                if (currentHash && currentHash === hash) {
                    var duplicate = {
                        sys_id: attGr.getValue('sys_id')
                    };
                    
                    if (includeMeta) {
                        duplicate.file_name = attGr.getValue('file_name');
                        duplicate.size_bytes = parseInt(attGr.getValue('size_bytes'));
                        duplicate.content_type = attGr.getValue('content_type');
                        duplicate.table_name = attGr.getValue('table_name');
                        duplicate.table_sys_id = attGr.getValue('table_sys_id');
                        duplicate.created = attGr.getValue('sys_created_on');
                        duplicate.created_by = attGr.getValue('sys_created_by');
                    }
                    
                    duplicates.push(duplicate);
                }
            }
            
        } catch (e) {
            gs.error('Error finding duplicates: ' + e.message);
        }
        
        return duplicates;
    }
    
})(inputs, outputs);

