sys_variable_value.value:
(function execute(inputs, outputs) {
    try {


        // Initialize GlideRecord for current and previous records
        var current = new GlideRecord(inputs.tableName);
        var previous = new GlideRecord(inputs.tableName);

        if (!current.get(inputs.currentSysId)) {
            throw new Error("Unable to retrieve current record with Sys ID: " + inputs.currentSysId);
        }

        if (!previous.get(inputs.previousSysId)) {
            throw new Error("Unable to retrieve previous record with Sys ID: " + inputs.previousSysId);
        }

        var fields = inputs.fieldsToCompare.split(',');
        var summary = [];

        fields.forEach(function(field) {
            var trimmedField = field.trim();
            if (!current.isValidField(trimmedField)) {
                summary.push("Field '" + trimmedField + "' is not valid for table '" + inputs.tableName + "'.");
                return;
            }

            var oldVal = previous.getValue(trimmedField);
            var newVal = current.getValue(trimmedField);

            if (oldVal !== newVal) {
                summary.push("Field '" + trimmedField + "' changed from '" + oldVal + "' to '" + newVal + "'");
            }
        });

        outputs.changesummary = summary.length > 0 ? summary.join('\n') : "No changes detected in specified fields.";
        outputs.error='';
    } catch (error) {
        // Log and rethrow the error for visibility in Flow Designer
        gs.error("Error in Generate Record Change Summary action: " + error.message);
        outputs.error="Error in Generate Record Change Summary action: " + error.message;
    }
})(inputs, outputs);

