sys_variable_value.value:
(function execute(inputs, outputs) {
    var scTaskID=inputs.sctask.getValue('sys_id');
    var ritmVariables=inputs.ritm.variables.getElements();
    ritmVariables.forEach(function(grvariable){
        grtaskvariable= new GlideRecord('sc_item_variables_task');
        grtaskvariable.task=scTaskID;
        grtaskvariable.variable=grvariable.getQuestion().id;
        grtaskvariable.insert();
        outputs.status = "Variable(s) added to Catalog Task"
    })
})(inputs, outputs);

