sys_variable_value.value:
(function execute(inputs, outputs) {

    try {
      var pausedFlowId = inputs.context;                       // Context ID of the flow in waiting for message state
      var resumeMessage = inputs.message;                     // Message that paused flow is waiting for
      var payload = inputs.payload || '';                           // Data to send back to flow
      var result = sn_fd.FlowAPI
      .sendMessage(pausedFlowId, resumeMessage, payload);    // Send a message to the paused flow
    } catch (ex) {
      var message = ex.getMessage();
      gs.error(message);
    }

  


})(inputs, outputs);

