sys_variable_value.value:
(function execute(inputs, outputs) {
    // Security Configuration: Restricted Tables (High-Privilege Access)
    var RESTRICTED_TABLES = [
        'sys_user',
        'sys_user_group',
        'sys_user_role',
        'sys_user_has_role',
        'sys_user_grmember',
        'sys_security_acl',
        'sys_security_acl_role',
        'sys_db_object',
        'sys_dictionary',
        'sys_script',
        'sys_script_include',
        'sys_ui_script',
        'sys_ui_action',
        'sys_business_rule',
        'sys_properties',
        'sys_scope',
        'sys_app',
        'sys_package',
        'oauth_entity',
        'oauth_credential',
        'sys_auth_profile_application'
    ];

    // Security Configuration: Blocked Operations
    var BLOCKED_OPERATIONS = [
        'deleteRecord',
        'deleteMultiple',
        'setWorkflow',
        'setAbortAction',
        'autoSysFields',
        'setNewGuidValue'
    ];

    // Security Configuration: Whitelisted Classes (Safe to instantiate)
    // Note: Some classes are excluded for scoped app compatibility
    var WHITELISTED_CLASSES = [
        'GlideRecord',
        'GlideDateTime',
        'GlideDate',
        'GlideTime',
        'GlideDuration',
        'GlideAggregate',
        'GlideElement',
        'GlideFilter',
        'GlideSchedule',
        'GlideStringUtil'
        // GlideEncrypter - Not allowed in scoped apps
        // GlideChecksum - May not be allowed in scoped apps
        // GlideDigest - May not be allowed in scoped apps
    ];

    // Security Configuration: Blocked Script Include Patterns
    var BLOCKED_SCRIPT_INCLUDE_PATTERNS = [
        'gs.include(',  // Block gs.include() calls
        'GlideScriptInclude'  // Block Script Include API
    ];

    // Security Configuration: High-Privilege Role/User sys_ids to block
    // These are well-known sys_ids that attackers might hardcode
    var BLOCKED_SYSIDS = [
        '2831a114c611228501d4ea6c309d626d',  // admin role
        '6816f79cc0a8016401c5a33be04be441',  // admin user
        '5d7e6b13c611227a0146101de1d70f5b',  // security_admin role
        '0c441abbc6112275000025157c2f22de'   // maint role (maintenance)
    ];

    // Initialize output variables
    outputs.success = false;
    outputs.result = '';
    outputs.error = '';
    outputs.execution_time_ms = 0;

    try {
        // Validate required inputs
        if (!inputs.script_input || inputs.script_input.trim() === '') {
            throw new Error('Script input is required and cannot be empty');
        }
        
        if (!inputs.temp_table || inputs.temp_table.trim() === '') {
            throw new Error('Temp table name is required (e.g., x_snc_actionpack_script_temp)');
        }
        
        if (!inputs.script_field || inputs.script_field.trim() === '') {
            throw new Error('Script field name is required (e.g., script)');
        }
        
        var tempTableName = inputs.temp_table.trim();
        var scriptFieldName = inputs.script_field.trim();
        
        // Optional audit field names (only use if provided)
        var executedByField = inputs.executed_by_field ? inputs.executed_by_field.trim() : null;
        var executedByNameField = inputs.executed_by_name_field ? inputs.executed_by_name_field.trim() : null;
        var executionTimeField = inputs.execution_time_field ? inputs.execution_time_field.trim() : null;
        
        // Validate that the table exists and is accessible
        var testTable = new GlideRecord(tempTableName);
        if (!testTable.isValid()) {
            throw new Error('Table "' + tempTableName + '" does not exist or is not accessible. Please create it in your scoped app.');
        }
        
        // Validate that the script field exists
        if (!testTable.isValidField(scriptFieldName)) {
            throw new Error('Field "' + scriptFieldName + '" does not exist on table "' + tempTableName + '". Please add a Script-type field with this name.');
        }

        var scriptToExecute = inputs.script_input.trim();
        var startTime = new GlideDateTime();

        // Security Check 1: Scan for restricted table access
        var scriptLower = scriptToExecute.toLowerCase();
        for (var i = 0; i < RESTRICTED_TABLES.length; i++) {
            var tableName = RESTRICTED_TABLES[i];
            // Check for GlideRecord instantiation or table references
            if (scriptLower.indexOf("'" + tableName + "'") !== -1 || 
                scriptLower.indexOf('"' + tableName + '"') !== -1 ||
                scriptLower.indexOf('`' + tableName + '`') !== -1) {
                throw new Error('SECURITY VIOLATION: Access to restricted table "' + tableName + '" is not allowed');
            }
        }

        // Security Check 1b: Block hardcoded high-privilege sys_ids
        // Prevents attackers from bypassing table checks by using direct sys_ids
        for (var s = 0; s < BLOCKED_SYSIDS.length; s++) {
            var sysId = BLOCKED_SYSIDS[s];
            if (scriptToExecute.indexOf(sysId) !== -1) {
                throw new Error('SECURITY VIOLATION: Reference to high-privilege sys_id is not allowed - prevents hardcoded admin/security role manipulation');
            }
        }

        // Security Check 2: Block delete operations
        if (scriptLower.indexOf('deleterecord') !== -1 || 
            scriptLower.indexOf('deletemultiple') !== -1) {
            throw new Error('SECURITY VIOLATION: Delete operations are not allowed');
        }

        // Security Check 3: Block dangerous GlideRecord methods
        for (var j = 0; j < BLOCKED_OPERATIONS.length; j++) {
            var operation = BLOCKED_OPERATIONS[j].toLowerCase();
            if (scriptLower.indexOf(operation) !== -1) {
                throw new Error('SECURITY VIOLATION: Operation "' + BLOCKED_OPERATIONS[j] + '" is not allowed');
            }
        }

        // Security Check 4: Block eval and Function constructor
        // Note: We allow regular function declarations/IIFEs, but block Function constructor
        if (scriptLower.indexOf('eval(') !== -1 ||
            scriptLower.indexOf('new function') !== -1) {
            throw new Error('SECURITY VIOLATION: Dynamic code evaluation (eval, Function constructor) is not allowed');
        }

        // Security Check 5: Block system-level operations and indirect code execution
        var dangerousPatterns = [
            'eval(',
            'new Function(',  // Block Function constructor, but allow regular function declarations
            'gs.executeNow(',
            'gs.eventQueue(',
            'gs.setProperty(',          // Prevents system property manipulation
            'Packages.java',
            'java.lang',
            'GlideScopedEvaluator',
            'GlideEvaluator',
            'SncTriggerSynchronizer',  // Prevents triggering scheduled jobs
            'GlideScheduler',           // Prevents scheduling jobs
            'GlideScriptedProcessor',   // Prevents processor execution
            'GlideScriptedHierarchicalWorker', // Prevents worker execution
            'sysauto',                  // Prevents scheduled job manipulation
            'sys_trigger',              // Prevents trigger manipulation
            'GlideController',          // Prevents UI action execution
            'GlideServlet',             // Prevents servlet access
            'GlideSession.get(',        // Prevents session hijacking
            'gs.getSession().impersonate', // Prevents impersonation
            'gs.getSession().setUser'   // Prevents session user manipulation
        ];

        for (var k = 0; k < dangerousPatterns.length; k++) {
            if (scriptLower.indexOf(dangerousPatterns[k].toLowerCase()) !== -1) {
                throw new Error('SECURITY VIOLATION: System-level operation "' + dangerousPatterns[k] + '" is not allowed');
            }
        }

        // Security Check 6: Block Script Include instantiation (indirect privilege escalation)
        // This prevents users from calling custom Script Includes that may have elevated privileges
        
        // First, block gs.include() and GlideScriptInclude
        for (var m = 0; m < BLOCKED_SCRIPT_INCLUDE_PATTERNS.length; m++) {
            var pattern = BLOCKED_SCRIPT_INCLUDE_PATTERNS[m].toLowerCase();
            if (scriptLower.indexOf(pattern) !== -1) {
                throw new Error('SECURITY VIOLATION: Script Include loading ("' + BLOCKED_SCRIPT_INCLUDE_PATTERNS[m] + '") is not allowed - prevents indirect privilege escalation');
            }
        }
        
        // Second, check for 'new' keyword usage and validate against whitelist
        var newKeywordRegex = /new\s+([A-Za-z_][A-Za-z0-9_]*)/gi;
        var matches = scriptToExecute.match(newKeywordRegex);
        
        if (matches) {
            for (var n = 0; n < matches.length; n++) {
                var match = matches[n];
                // Extract class name after 'new'
                var className = match.replace(/new\s+/i, '').trim();
                
                // Check if class is in whitelist
                var isWhitelisted = false;
                for (var w = 0; w < WHITELISTED_CLASSES.length; w++) {
                    if (className.toLowerCase() === WHITELISTED_CLASSES[w].toLowerCase()) {
                        isWhitelisted = true;
                        break;
                    }
                }
                
                if (!isWhitelisted) {
                    throw new Error('SECURITY VIOLATION: Instantiation of non-whitelisted class "' + className + '" is not allowed. Only ServiceNow API classes (GlideRecord, GlideDateTime, etc.) are permitted - prevents indirect privilege escalation via Script Includes');
                }
            }
        }

        // Execute script with controlled scope
        // Note: GlideSandbox is not available in Flow Designer scope, so we use
        // GlideScopedEvaluator for scoped app compatibility
        var executionResult;

        try {
            // Create isolated execution context
            // We pass only whitelisted variables to limit scope access
            
            try {
                // Use GlideScopedEvaluator for scoped app compatibility
                // IMPORTANT: GlideScopedEvaluator runs in system context for security isolation
                // This is mitigated by the 8 layers of security checks above
                
                // Check if script is already wrapped in a function
                var scriptTrimmed = scriptToExecute.trim();
                var isAlreadyWrapped = scriptTrimmed.match(/^\(?function\s*\(/i) || 
                                       scriptTrimmed.match(/^\(\s*function\s*\(/i);
                
                // Wrap script in IIFE to support return statements (if not already wrapped)
                var wrappedScript = isAlreadyWrapped ? scriptToExecute : '(function() { ' + scriptToExecute + ' })()';
                
                // Create a temporary record in the specified table to hold the script
                var tempScript = new GlideRecord(tempTableName);
                tempScript.initialize();
                tempScript.setValue(scriptFieldName, wrappedScript);
                
                // Add audit information if field names were provided and fields exist
                if (executedByField && tempScript.isValidField(executedByField)) {
                    tempScript.setValue(executedByField, gs.getUserID());
                }
                if (executedByNameField && tempScript.isValidField(executedByNameField)) {
                    tempScript.setValue(executedByNameField, gs.getUserName());
                }
                if (executionTimeField && tempScript.isValidField(executionTimeField)) {
                    tempScript.setValue(executionTimeField, new GlideDateTime());
                }
                
                // Insert the record - GlideScopedEvaluator needs a persisted record
                var tempSysId = tempScript.insert();
                
                // Re-query to get the actual record
                tempScript = new GlideRecord(tempTableName);
                tempScript.get(tempSysId);
                
                // Create evaluator
                var evaluator = new GlideScopedEvaluator();
                
                // Put variables into the evaluator context
                // Note: We don't pass GlideRecord and other classes as they're globally available
                // Passing them as variables can cause scope issues with methods like setLimit()
                var variables = {
                    inputs: inputs,
                    outputs: outputs,
                    gs: gs
                };
                
                // Execute the script from the temporary record
                // evaluateScript(GlideRecord, scriptField, variables)
                executionResult = evaluator.evaluateScript(tempScript, scriptFieldName, variables);
                
                // Clean up: Delete the temporary record
                if (tempSysId) {
                    tempScript.deleteRecord();
                }
                
            } finally {
                // Cleanup handled above
            }
            
        } catch (executionError) {
            throw new Error('Script execution failed: ' + executionError.message);
        }

        // Calculate execution time
        var endTime = new GlideDateTime();
        var duration = GlideDateTime.subtract(startTime, endTime);
        outputs.execution_time_ms = duration.getNumericValue();

        // Compile results
        var resultOutput = '';
        
        if (executionResult !== undefined && executionResult !== null) {
            resultOutput = 'Return Value: ' + String(executionResult);
        }

        outputs.success = true;
        outputs.result = resultOutput || 'Script executed successfully with no return value. End your script with the expression you want to return.';
        
        // Log successful execution for audit
        gs.info('Flow Action - Run Script: Executed successfully by ' + gs.getUserName() + 
                ' | Duration: ' + outputs.execution_time_ms + 'ms');

    } catch (error) {
        outputs.success = false;
        outputs.error = 'Error: ' + error.message;
        outputs.result = '';
        
        // Log security violations and errors for audit
        if (error.message.indexOf('SECURITY VIOLATION') !== -1) {
            gs.error('Flow Action - Run Script: SECURITY VIOLATION by ' + gs.getUserName() + 
                     ' | Error: ' + error.message);
        } else {
            gs.warn('Flow Action - Run Script: Execution failed for ' + gs.getUserName() + 
                    ' | Error: ' + error.message);
        }
    }

})(inputs, outputs);

