        for (var j = 0; j < BLOCKED_OPERATIONS.length; j++) {
            var operation = BLOCKED_OPERATIONS[j].toLowerCase();
            if (scriptLower.indexOf(operation) !== -1) {
                throw new Error('SECURITY VIOLATION: Operation "' + BLOCKED_OPERATIONS[j] + '" is not allowed');
