            'eval(',
            'new Function(',  // Block Function constructor, but allow regular function declarations
            'gs.executeNow(',
            'gs.eventQueue(',
            'gs.setProperty(',          // Prevents system property manipulation
            'Packages.java',
            'java.lang',
            'GlideScopedEvaluator',
            'GlideEvaluator',
            'SncTriggerSynchronizer',  // Prevents triggering scheduled jobs
            'GlideScheduler',           // Prevents scheduling jobs
            'GlideScriptedProcessor',   // Prevents processor execution
            'GlideScriptedHierarchicalWorker', // Prevents worker execution
            'sysauto',                  // Prevents scheduled job manipulation
            'sys_trigger',              // Prevents trigger manipulation
            'GlideController',          // Prevents UI action execution
            'GlideServlet',             // Prevents servlet access
            'GlideSession.get(',        // Prevents session hijacking
            'gs.getSession().impersonate', // Prevents impersonation
            'gs.getSession().setUser'   // Prevents session user manipulation
        ];

        for (var k = 0; k < dangerousPatterns.length; k++) {
            if (scriptLower.indexOf(dangerousPatterns[k].toLowerCase()) !== -1) {
                throw new Error('SECURITY VIOLATION: System-level operation "' + dangerousPatterns[k] + '" is not allowed');
            }
