        // This prevents users from calling custom Script Includes that may have elevated privileges
        
        // First, block gs.include() and GlideScriptInclude
        for (var m = 0; m < BLOCKED_SCRIPT_INCLUDE_PATTERNS.length; m++) {
            var pattern = BLOCKED_SCRIPT_INCLUDE_PATTERNS[m].toLowerCase();
            if (scriptLower.indexOf(pattern) !== -1) {
