            for (var n = 0; n < matches.length; n++) {
                var match = matches[n];
                // Extract class name after 'new'
                var className = match.replace(/new\s+/i, '').trim();
                
                // Check if class is in whitelist
                var isWhitelisted = false;
                for (var w = 0; w < WHITELISTED_CLASSES.length; w++) {
                    if (className.toLowerCase() === WHITELISTED_CLASSES[w].toLowerCase()) {
                        isWhitelisted = true;
                        break;
                    }
                }
                
                if (!isWhitelisted) {
                    throw new Error('SECURITY VIOLATION: Instantiation of non-whitelisted class "' + className + '" is not allowed. Only ServiceNow API classes (GlideRecord, GlideDateTime, etc.) are permitted - prevents indirect privilege escalation via Script Includes');
                }
            }
        }

        // Execute script with controlled scope
        // Note: GlideSandbox is not available in Flow Designer scope, so we use
        // GlideScopedEvaluator for scoped app compatibility
        var executionResult;

        try {
            // Create isolated execution context
            // We pass only whitelisted variables to limit scope access
            
            try {
                // Use GlideScopedEvaluator for scoped app compatibility
                // IMPORTANT: GlideScopedEvaluator runs in system context for security isolation
                // This is mitigated by the 8 layers of security checks above
                
                // Check if script is already wrapped in a function
                var scriptTrimmed = scriptToExecute.trim();
                var isAlreadyWrapped = scriptTrimmed.match(/^\(?function\s*\(/i) || 
                                       scriptTrimmed.match(/^\(\s*function\s*\(/i);
                
