                var tempScript = new GlideRecord(tempTableName);
                tempScript.initialize();
                tempScript.setValue(scriptFieldName, wrappedScript);
                
                // Add audit information if field names were provided and fields exist
                if (executedByField && tempScript.isValidField(executedByField)) {
