                    tempScript.setValue(executionTimeField, new GlideDateTime());
                }
                
                // Insert the record - GlideScopedEvaluator needs a persisted record
                var tempSysId = tempScript.insert();
                
                // Re-query to get the actual record
                tempScript = new GlideRecord(tempTableName);
                tempScript.get(tempSysId);
                
                // Create evaluator
                var evaluator = new GlideScopedEvaluator();
                
                // Put variables into the evaluator context
                // Note: We don't pass GlideRecord and other classes as they're globally available
                // Passing them as variables can cause scope issues with methods like setLimit()
                var variables = {
                    inputs: inputs,
                    outputs: outputs,
                    gs: gs
                };
                
                // Execute the script from the temporary record
                // evaluateScript(GlideRecord, scriptField, variables)
                executionResult = evaluator.evaluateScript(tempScript, scriptFieldName, variables);
                
                // Clean up: Delete the temporary record
                if (tempSysId) {
                    tempScript.deleteRecord();
                }
                
            } finally {
                // Cleanup handled above
            }
            
        } catch (executionError) {
            throw new Error('Script execution failed: ' + executionError.message);
        }

        // Calculate execution time
        var endTime = new GlideDateTime();
        var duration = GlideDateTime.subtract(startTime, endTime);
        outputs.execution_time_ms = duration.getNumericValue();

        // Compile results
        var resultOutput = '';
        
        if (executionResult !== undefined && executionResult !== null) {
            resultOutput = 'Return Value: ' + String(executionResult);
        }

        outputs.success = true;
        outputs.result = resultOutput || 'Script executed successfully with no return value. End your script with the expression you want to return.';
        
        // Log successful execution for audit
        gs.info('Flow Action - Run Script: Executed successfully by ' + gs.getUserName() + 
                ' | Duration: ' + outputs.execution_time_ms + 'ms');

    } catch (error) {
        outputs.success = false;
        outputs.error = 'Error: ' + error.message;
        outputs.result = '';
        
        // Log security violations and errors for audit
        if (error.message.indexOf('SECURITY VIOLATION') !== -1) {
            gs.error('Flow Action - Run Script: SECURITY VIOLATION by ' + gs.getUserName() + 
                     ' | Error: ' + error.message);
        } else {
            gs.warn('Flow Action - Run Script: Execution failed for ' + gs.getUserName() + 
                    ' | Error: ' + error.message);
        }
    }

})(inputs, outputs);

