sys_variable_value.value:
(function execute(inputs, outputs) {
    var method = (inputs.method || 'GET').toUpperCase();
    var url = (inputs.endpoint_url || '').trim();
    var credId = (inputs.credential_id || '').trim();
    var headersJson = inputs.headers_json || '{}';
    var bodyJson = inputs.body_json || '';
    var timeout = parseInt(inputs.timeout_sec, 10) || 30;
    var logResult = !!inputs.log_result;

    if (!url) {
        outputs.error_message = "Missing endpoint_url input";
        return;
    }

    var r = new sn_ws.RESTMessageV2();
    r.setHttpMethod(method);
    r.setEndpoint(url);

    // Handle headers
    try {
        var headers = JSON.parse(headersJson);
        for (var h in headers) {
            r.setRequestHeader(h, headers[h]);
        }
    } catch (e) {
        outputs.error_message = "Invalid headers JSON: " + e.message;
        return;
    }

    // Set credentials if provided
    if (credId) {
        r.setEccParameter('basic_credential', credId);
    }

    // Handle body
    if (bodyJson && (method === 'POST' || method === 'PUT')) {
        r.setRequestBody(bodyJson);
    }

    // Timeout
    r.setHttpTimeout(timeout);

    var start = new GlideDateTime();
    try {
        var res = r.execute();
        var duration = GlideDateTime.subtract(new GlideDateTime(), start).getNumericValue();

        var responseBody = res.getBody();
        var status = res.getStatusCode();
        var parsedBody;

        try {
            parsedBody = JSON.parse(responseBody);
        } catch (e) {
            parsedBody = {};
        }

        // Set outputs
        outputs.status_code = status;
        outputs.response_body = responseBody;
        outputs.response_json = JSON.stringify(parsedBody);
        outputs.duration_ms = duration;
        outputs.error_message = "";

        // Optional logging
        if (logResult) {
            var log = new GlideRecord("x_snc_dynamic_integration_log");
            log.initialize();
            log.setValue("u_endpoint", url);
            log.setValue("u_method", method);
            log.setValue("u_status", status);
            log.setValue("u_duration", duration);
            log.setValue("u_response", responseBody);
            log.insert();
        }

    } catch (err) {
        outputs.error_message = "REST execution failed: " + err.message;
    }
})(inputs, outputs);

