sys_variable_value.value:
(function execute(inputs, outputs) {
    outputs.jsondatastring = '';
    outputs.resultmessage = 'Processing started...';

    var attachmentSysId = inputs.attachmentSysId;
    // This array will hold the results, potentially one object per sheet.
    var allSheetData = []; 
    var totalRecords = 0;

    // 1. Retrieve Attachment Record and Metadata
    var attGR = new GlideRecord('sys_attachment');
    if (!attGR.get(attachmentSysId)) {
        outputs.resultmessage = 'Error: Attachment not found for sys_id: ' + attachmentSysId;
        return;
    }

    var fileName = attGR.file_name.toLowerCase();
    var mimeType = attGR.content_type.toLowerCase();
    
    var isExcel = fileName.endsWith('.xlsx') && mimeType.indexOf('sheet') > -1;
    var isCSV = fileName.endsWith('.csv') || mimeType.indexOf('csv') > -1 || mimeType.indexOf('text') > -1;
    
    try {
        // === OPTION 1: Dedicated GlideExcelParser for true .xlsx files (Multi-Sheet Logic) ===
        if (isExcel) {
            
            var attachment = new GlideSysAttachment();
            var attachmentStream = attachment.getContentStream(attachmentSysId);
            
            if (!attachmentStream) {
                outputs.resultmessage = 'Error: Could not retrieve content stream for the Excel attachment.';
                return;
            }

            var parser = new sn_impex.GlideExcelParser();
            parser.setSource(attachmentStream);
            
            var sheetNames = parser.getSheetNames();
            
            // Loop through ALL sheet names found in the Excel file
            for (var s = 0; s < sheetNames.length; s++) {
                var currentSheetName = sheetNames[s];
                var sheetRecords = [];

                parser.setSheetName(currentSheetName); 

                if (parser.parse()) {
                    var headers = parser.getColumnHeaders();
                    
                    while (parser.next()) {
                        var rowData = parser.getRow();
                        var jsonRow = {};
                        
                        for (var i = 0; i < headers.length; i++) {
                            var header = headers[i];
                            var jsonKey = header.replace(/[^a-zA-Z0-9_]/g, '').trim(); 
                            jsonRow[jsonKey] = rowData[header];
                        }
                        sheetRecords.push(jsonRow);
                        totalRecords++;
                    }
                    
                    // Add the processed sheet to the final output array
                    allSheetData.push({
                        "SheetName": currentSheetName,
                        "Records": sheetRecords
                    });

                } else {
                    // Log error for a specific sheet but continue parsing others
                    gs.warn("Error parsing sheet " + currentSheetName + ": " + parser.getErrorMessage());
                }
            }

        // === OPTION 2: String Parsing for CSV files (Single Sheet Logic) ===
        // Note: For the CSV files you attached, they represent only ONE sheet (the file itself)
        } else if (isCSV) {
            var sheetRecords = [];
            var contentBytes = new GlideSysAttachment().getContent(attGR);
            var csvString = String(contentBytes);

            var lines = csvString.replace(/\r/g, '').split('\n');
            
            if (lines.length < 2) {
                outputs.resultmessage = 'Error: CSV file is empty or contains only a header line.';
                return;
            }
            
            var headers = lines[0].split(',');
            var sanitizedHeaders = [];
            for (var h = 0; h < headers.length; h++) {
                sanitizedHeaders.push(headers[h].trim().replace(/[^a-zA-Z0-9_]/g, ''));
            }

            for (var i = 1; i < lines.length; i++) {
                var currentLine = lines[i].trim();
                if (currentLine === '') continue; 

                var values = currentLine.split(',');
                var jsonRow = {};
                
                for (var j = 0; j < sanitizedHeaders.length; j++) {
                    var key = sanitizedHeaders[j];
                    jsonRow[key] = (values[j] || '').trim();
                }
                
                sheetRecords.push(jsonRow);
                totalRecords++;
            }
            
            // Add the single CSV file result to the final output array
            allSheetData.push({
                "SheetName": "Single_CSV_Sheet",
                "Records": sheetRecords
            });

        } else {
            outputs.resultmessage = 'Error: Unsupported file format. File Name: ' + attGR.file_name;
            return;
        }

        // 3. Set Success Outputs
        // The final JSON string is now an array containing sheet objects.
        outputs.jsondatastring = JSON.stringify(allSheetData);
        outputs.resultmessage = 'Success: ' + totalRecords + ' records parsed across ' + allSheetData.length + ' sheets from file: ' + attGR.file_name;

    } catch (ex) {
        outputs.resultmessage = 'A critical scripting exception occurred: ' + ex.getMessage();
    }

})(inputs, outputs);

