sys_variable_value.value:
(function execute(inputs, outputs) {

    var gr = new GlideRecord("sys_user_grmember");
    gr.addQuery("user", inputs.user);
    gr.addQuery("group", inputs.group);
    gr.query();

    if (gr.hasNext()) {
        outputs.is_member = true;
    } else {
        outputs.is_member = false;
    }

})(inputs, outputs);

