sys_variable_value.value:
(function execute(inputs, outputs) {
    var userGr = inputs.users; // Input of type List (Users) is of type GlideRecord object
    var process_errors = [];
    while(userGr._next()){
        // Build preference record
        var preference = {
            name: inputs.preference_name,
            user: userGr.getValue("sys_id"),
            type: inputs.preference_type,
            value: inputs.preference_value
        };
        
        try{
            // Check if the preference already exists, so that we can deicde to insert or update
            var prefGr = new GlideRecord("sys_user_preference");
            prefGr.addQuery("name", inputs.preference_name);
            prefGr.addQuery("user", userGr.getValue("sys_id"));
            prefGr._query();
            var pref_record;
            if(prefGr._next()){ // Update existing record
                // GlideQuery is my personal preference for Quickly diagnose query errors with additional checks and clear error messages.
                pref_record = new global.GlideQuery('sys_user_preference')
                .where('sys_id', prefGr.getValue('sys_id'))
                .update(preference);
            }else{ // Insert new record
                pref_record = new global.GlideQuery('sys_user_preference')
                .insert(preference)
                .get();
            }
       } catch(ex){ // Catch and log errors
            process_errors.push(
                {
                    user_sys_id: userGr.getValue('sys_id'),
                    user_full_name: userGr.getDisplayValue('name'),
                    error: ex
                }
            );
        }
    }
    outputs.errors = process_errors;
})(inputs, outputs);

