sys_variable_value.value:
(function execute(inputs, outputs) {
    try {

        var tableName = inputs.target_table;
        var matchKeys = inputs.match_keys.split(',').map(function(k) { return k.trim(); });
        var records;
try {
    records = JSON.parse(inputs.record_array);
    if (!Array.isArray(records)) {
        throw new Error("Parsed input is not an array.");
    }
} catch (parseErr) {
    
    throw new Error("Invalid JSON format for 'record_array': " + parseErr.message);
}

 


        var insertCount = 0;
        var updateCount = 0;
        var errorLog = [];

        var gr = new GlideRecord(tableName);
        if (!gr.isValid()) throw new Error("Invalid target table: " + tableName);

        records.forEach(function(record, index) {
            try {
                // Build query to find matching record
                var matchGR = new GlideRecord(tableName);
                matchGR.initialize();
                var queryParts = [];

                matchKeys.forEach(function(key) {
                    if (!matchGR.isValidField(key)) throw new Error("Invalid match key: " + key);
                    if (!record.hasOwnProperty(key)) throw new Error("Record missing match key: " + key);
                    queryParts.push(key + "=" + record[key]);
                });

                matchGR.addEncodedQuery(queryParts.join('^'));
                matchGR.query();

                if (matchGR.next()) {
                    // Update existing record
                    for (var field in record) {
                        if (matchGR.isValidField(field)) {
                            matchGR.setValue(field, record[field]);
                        }
                    }
                    matchGR.update();
                    updateCount++;
                } else {
                    // Insert new record
                    var newGR = new GlideRecord(tableName);
                    newGR.initialize();
                    for (var field in record) {
                        if (newGR.isValidField(field)) {
                            newGR.setValue(field, record[field]);
                        }
                    }
                    newGR.insert();
                    insertCount++;
                }
            } catch (recordErr) {
                errorLog.push("Record " + index + ": " + recordErr.message);
            }
        });

        outputs.insert_count = insertCount;
        outputs.update_count = updateCount;
        outputs.error_log = errorLog;

    } catch (err) {
        gs.error("Bulk Upsert Error: " + err.message);
        outputs.insert_count = 0;
        outputs.update_count = 0;
        outputs.error_log = [err.message];
    }
})(inputs, outputs);

