sys_variable_value.value:
(function execute(inputs, outputs) {

    var cleanName = inputs.employeeName.replace(/,/g, '').trim();

    var nameParts = cleanName.split(/\s+/);

    var initials = '';
    for (var i = 0; i < nameParts.length; i++) {
        if (nameParts[i].length > 0) {
            initials += nameParts[i][0].toUpperCase();
        }
    }

    // Create email alias
    var emailAlias = initials.toLowerCase() + '.' + inputs.employeeId.toLowerCase() + '@company.com';

    // Set outputs
    outputs.emailalias = emailAlias;

})(inputs, outputs);

