sys_variable_value.value:
(function execute(inputs, outputs) {
    try {
        var contextId = inputs.flow_context_id || '';
        if (!contextId) {
            outputs.debug_json = JSON.stringify({ error: "Missing flow_context_id" });
            return;
        }

        var result = {
            flow_context: contextId,
            timestamp: new GlideDateTime().getDisplayValue(),
            logs: [],
            syslog: ["Syslog access restricted in scoped apps. Use GlobalLogReader if deeper logs needed."],
            execution_tracker: {}
        };

        // 1️⃣ Fetch Flow logs
        var fl = new GlideRecord("sys_flow_log");
        fl.addQuery("context", contextId);
        fl.orderBy("sys_created_on");
        fl.query();
        while (fl.next()) {
            result.logs.push({
                action: fl.action_display + '' || '',
                message: fl.message + '' || '',
                state: fl.state + '' || '',
                created_on: fl.sys_created_on.getDisplayValue()
            });
        }

        // 2️⃣ Add Execution Tracker (optional)
        if (inputs.include_tracker) {
            var tracker = new GlideRecord("sys_execution_tracker");
            tracker.addQuery("context_id", contextId);
            tracker.orderByDesc("sys_created_on");
            tracker.query();
            if (tracker.next()) {
                result.execution_tracker = {
                    state: tracker.state + '',
                    progress: tracker.progress_message + '',
                    started_on: tracker.start_time.getDisplayValue(),
                    duration: tracker.duration.getDisplayValue(),
                    result_message: tracker.result_message + ''
                };
            }
        }

        // 3️⃣ Output JSON
        var jsonStr = JSON.stringify(result, null, 2);
        outputs.debug_json = jsonStr;
        outputs.log_count = result.logs.length;

        // 4️⃣ Optional: attach as file
        if (inputs.attach_to_record && inputs.output_format === "File") {
            var attachment = new GlideSysAttachment();
            var fileName = "FlowDebug_" + contextId + ".json";
            var sysId = attachment.write(inputs.attach_to_record, fileName, "application/json", jsonStr);
            outputs.attachment_sys_id = sysId;
        }

    } catch (err) {
        var safeMsg = (err && err.toString) ? err.toString() : 'Unknown Script Error';
        outputs.debug_json = JSON.stringify({ error: safeMsg });
    }
})(inputs, outputs);

