sys_variable_value.value:
(function execute(inputs, outputs) {
    try {
        // normalize inputs
        var tableName = (inputs.table_name || '') + '';
        var r1 = (inputs.record1_sys_id || '') + '';
        var r2 = (inputs.record2_sys_id || '') + '';

        var includeNulls = !!inputs.include_nulls;
        var displayValues = inputs.display_values === false ? false : true; // default true
        var syncMode = (inputs.sync_mode || 'compare_only') + ''; // compare_only, update_target, delete_target
        var fieldsToSync = (inputs.fields_to_sync || '') + ''; // optional, comma-separated list

        if (!tableName || !r1 || !r2) {
            outputs.differences_json = JSON.stringify({
                error: "Missing required inputs: table_name, record1_sys_id, record2_sys_id"
            });
            return;
        }

        // build ignore list
        var ignoreList = {};
        ['sys_created_by', 'sys_created_on', 'sys_updated_by', 'sys_updated_on', 'sys_id'].forEach(function(f) {
            ignoreList[f] = true;
        });

        // load records securely
        var gr1 = new GlideRecordSecure(tableName);
        if (!gr1.get(r1)) {
            outputs.differences_json = JSON.stringify({ error: "Record 1 not found: " + r1 });
            return;
        }
        var gr2 = new GlideRecordSecure(tableName);
        if (!gr2.get(r2)) {
            outputs.differences_json = JSON.stringify({ error: "Record 2 not found: " + r2 });
            return;
        }

        // determine allowed fields (if user passed fields_to_sync)
        var allowedFields = {};
        if (fieldsToSync) {
            fieldsToSync.split(',').map(function(f) { return f.trim(); }).forEach(function(f) {
                if (f) allowedFields[f] = true;
            });
        }

        var result = {};
        var changedFields = [];

        // iterate through dictionary for this table
        var dict = new GlideRecord('sys_dictionary');
        dict.addQuery('name', tableName);
        dict.addQuery('internal_type', '!=', 'collection');
        dict.addQuery('internal_type', '!=', 'gliderecord');
        dict.query();

        while (dict.next()) {
            var field = dict.element.toString();
            if (ignoreList[field]) continue;

            try {
                var val1 = gr1.getValue(field);
                var val2 = gr2.getValue(field);

                // skip both null unless includeNulls = true
                if (!includeNulls && (val1 === '' || val1 === null) && (val2 === '' || val2 === null)) continue;

                // skip identical values
                if (val1 == val2) continue;

                var out1 = displayValues ? gr1.getDisplayValue(field) : val1;
                var out2 = displayValues ? gr2.getDisplayValue(field) : val2;

                // glide_list normalization
                var internalType = dict.internal_type + '';
                if (internalType === 'glide_list' || (internalType === 'string' && field.indexOf('list') !== -1)) {
                    var a1 = (val1 || '').split(',').map(function(s) { return s.trim(); }).filter(Boolean).sort();
                    var a2 = (val2 || '').split(',').map(function(s) { return s.trim(); }).filter(Boolean).sort();
                    if (a1.join('|') === a2.join('|')) continue;
                    out1 = a1.join(', ');
                    out2 = a2.join(', ');
                }

                result[field] = [out1, out2];
                changedFields.push(field);

            } catch (innerErr) {
                result[field] = ["<read_error>", "<read_error>"];
            }
        }

        // handle sync actions (update_target or delete_target)
        var syncSummary = "";
        if (syncMode === 'update_target' && changedFields.length > 0) {
            var updateCount = 0;
            changedFields.forEach(function(f) {
                if (allowedFields && Object.keys(allowedFields).length > 0 && !allowedFields[f]) return;
                gr2.setValue(f, gr1.getValue(f));
                updateCount++;
            });
            gr2.update();
            syncSummary = "Updated Record 2 (" + r2 + ") with " + updateCount + " field(s) from Record 1 (" + r1 + ")";
        } else if (syncMode === 'delete_target') {
            gr2.deleteRecord();
            syncSummary = "Deleted Record 2 (" + r2 + ")";
        } else {
            syncSummary = "Comparison only - no sync performed";
        }

        // final outputs
        outputs.differences_json = JSON.stringify(result);
        outputs.sync_summary = syncSummary;
        outputs.fields_changed = changedFields.length;

    } catch (err) {
        outputs.differences_json = JSON.stringify({ error: err.message });
    }
})(inputs, outputs);

