sys_element_mapping.value:
(function execute(inputs, outputs) {
/**
 * Calculate commission based on sale amount, region, and agent tier.
 * - Base tiers (by amount, USD):
 *   < 1,000 → 3%
 *   1,000–4,999.99 → 5%
 *   5,000–49,999.99 → 7%
 *   ≥ 50,000 → 10%
 *
 * - Region adders (percentage points):
 *   NA +5%, EMEA +6%, LATAM +4%, APAC +3%
 *
 * - Agent tier adders (percentage points):
 *   Tier 1 +7%, Tier 2 +4%, Tier 3 +2%, Tier 4 +0%
 *
 * @param {number} amount - Sale amount in USD
 * @param {string} region - One of: 'NA','EMEA','LATAM','APAC' (case-insensitive)
 * @param {number|string} tier - 1,2,3,4 (number or numeric string)
 * @returns {{commissionRate:number, saleAmount:number, commissionAmount:number, detail:string}}
 */
//function calculateCommission(amount, region, tier) {

  // Normalize/validate inputs
  var amt = Number(inputs.saleAmount);
  if (!isFinite(amt) || amt < 0) amt = 0;

  var reg = String(inputs.region || '').trim().toUpperCase();
  var tierNum = parseInt(inputs.tier, 10);
  if (![1,2,3,4].includes(tierNum)) tierNum = 4; // default to no extra if invalid

  // Base rate by amount
  var baseRate;
  if (amt < 1000) baseRate = 0.03;
  else if (amt < 5000) baseRate = 0.05;
  else if (amt < 50000) baseRate = 0.07;
  else baseRate = 0.10;

  // Region adders
  var regionAdders = {
    'NA': 0.05,
    'EMEA': 0.06,
    'LATAM': 0.04,
    'APAC': 0.03
  };
  var regionAdder = regionAdders[reg] || 0;

  // Tier adders
  var tierAdders = {
    1: 0.07,
    2: 0.04,
    3: 0.02,
    4: 0.00
  };
  var tierAdder = tierAdders[tierNum];

  // Final rate (additive)
  var commissionRate = baseRate + regionAdder + tierAdder;

  // Commission amount
  var commissionAmount = amt * commissionRate;

  // Rounding helpers
  function round(n, places) {
    var p = Math.pow(10, places);
    return Math.round((n + Number.EPSILON) * p) / p;
  }

  var result = {
    commissionRate: round(commissionRate, 4),     // e.g., 0.1900 for 19%
    saleAmount: round(amt, 2),
    commissionAmount: round(commissionAmount, 2),
    detail: [
      'base=' + (baseRate*100).toFixed(2) + '%',
      'region=' + reg + ' +' + (regionAdder*100).toFixed(2) + 'pp',
      'tier=' + tierNum + ' +' + (tierAdder*100).toFixed(2) + 'pp'
    ].join(' | ')
  };
outputs.commission_rate = result.commissionRate
outputs.sale_amount = result.saleAmount
outputs.commission_amount = result.commissionAmount

})(inputs, outputs);

