sys_variable_value.value:
(function execute(inputs, outputs) {


var userInputInSeconds = inputs.inputSecond;
var glideDurationBackendFieldName = inputs.glideDurationBackendName;
var targetTable = inputs.targetTable;
var recordSysId = inputs.recordSysID;
var isUpdated = 'false';
var grRecord = new GlideRecord(targetTable);
if(!grRecord.isValidField(glideDurationBackendFieldName)){
    return;
}
if (grRecord.get(recordSysId)) {
    var seconds = parseInt(userInputInSeconds, 10);
    if (!isNaN(seconds) && seconds > 0) {
        // convert seconds → milliseconds → GlideDuration
        var ms = seconds * 1000;
        var gd = new GlideDuration(ms);


        //set the backend name of the field
        grRecord.setValue(glideDurationBackendFieldName, gd.getValue());
        grRecord.update();
        isUpdated = 'true';


    }
}
outputs.executed = isUpdated;




})(inputs, outputs);

