sys_variable_value.value:
(function execute(inputs, outputs) {
    try {
        var userSysId = inputs.user_sys_id;
        var maxLevels = parseInt(inputs.levels, 10) || 1;
        var matrix = [];

        // Validate user_sys_id
        if (!userSysId) {
            gs.error('Generate Approval Matrix: Missing required input [user_sys_id]');
            outputs.approval_matrix = JSON.stringify(matrix);
            return;
        }

        // Validate that user record exists
        var currentUser = new GlideRecord('sys_user');
        if (!currentUser.get(userSysId)) {
            gs.error('Generate Approval Matrix: Invalid user record for Sys ID: ' + userSysId);
            outputs.approval_matrix = JSON.stringify(matrix);
            return;
        }

        var level = 1;
        var managerId = currentUser.manager;

        while (managerId && level <= maxLevels) {
            var manager = new GlideRecord('sys_user');

            try {
                if (manager.get(managerId)) {
                    // Safely access fields
                    var approverName = manager.name ? manager.name.toString() : 'Unknown';
                    var approverEmail = manager.email ? manager.email.toString() : 'No Email';

                    matrix.push({
                        level: level,
                        approver_sys_id: manager.getUniqueValue(),
                        approver_name: approverName,
                        approver_email: approverEmail
                    });

                    // Move up the hierarchy
                    managerId = manager.manager;
                    level++;
                } else {
                    gs.warn('Generate Approval Matrix: Manager record not found for Sys ID: ' + managerId);
                    break;
                }
            } catch (innerErr) {
                gs.error('Generate Approval Matrix: Error while processing manager ID ' + managerId + ' - ' + innerErr.message);
                break;
            }
        }

        outputs.approval_matrix = JSON.stringify(matrix);

    } catch (err) {
        // Catch any unexpected runtime errors
        gs.error('Generate Approval Matrix: Unexpected error occurred - ' + err.message);
        outputs.approval_matrix = JSON.stringify([]);
    }
})(inputs, outputs);

