sys_variable_value.value:
(function execute(inputs, outputs) {
    try{
        var glide_schedule = new GlideSchedule(inputs.schedule);
        var time_zone = inputs.time_zone;
        if(!gs.nil(time_zone)){ // Set time zone if value is specified
            glide_schedule.setTimeZone(time_zone);
        }
        var start_date = new GlideDateTime(inputs.start_date);
        var end_date = new GlideDateTime(inputs.end_date);
        var duration = glide_schedule.duration(start_date, end_date);
        duration_value = duration.getValue() + "";
        duration_display_value = duration.getDisplayValue();

        var days = 0;
        var hours = 0;
        var minutes = 0;
        var seconds = 0;        

        var dayMatch = duration_display_value.match(/(\d+)\s*days?/i);
        var hourMatch = duration_display_value.match(/(\d+)\s*hours?/i);
        var minuteMatch = duration_display_value.match(/(\d+)\s*minutes?/i);
        var secondMatch = duration_display_value.match(/(\d+)\s*seconds?/i);

        if (dayMatch) days = parseInt(dayMatch[1], 10);
        if (hourMatch) hours = parseInt(hourMatch[1], 10);
        if (minuteMatch) minutes = parseInt(minuteMatch[1], 10);
        if (secondMatch) seconds = parseInt(secondMatch[1], 10);

        gs.info(seconds);
        // See if there is a value in duration value, because getDisplayValue tends to ignore seconds
        if(seconds < 1){
            var parts = duration_value.split(" ")[1].split(":"); // Get time part and split by ":"
            seconds = parseInt(parts[2], 10); // Get seconds as integer
            gs.info(seconds);
        }
        outputs.total_milli_seconds = duration.getNumericValue() + ""; // To string
        outputs.days = days;
        outputs.hours = hours;
        outputs.minutes = minutes;
        outputs.seconds = seconds;
        outputs.display_value = days + " Days " + hours + " Hours " + minutes + " minutes " + seconds + " seconds";
    } catch(ex){
        outputs.error = ex + "";
    }

})(inputs, outputs);

